export default class Stepper {

  result;
  speed = 500;
  interval;

  constructor() {
  }

  getAlgorithm() {
    return this.algorithm;
  }

  reset() {
    this.pause();
    this.result = null;
  }

  setResult(result) {
    this.reset();
    this.result = result;
  }

  updateStepGUI(description, stepNum, stepTotal) {
    let des = document.getElementsByClassName('algorithm-step-des');
    let num = document.getElementsByClassName('algorithm-step-num');

    if (des.length === 1 && num.length === 1) {
      des[0].innerHTML = 'Description:  ' + description;
      num[0].innerHTML = 'Step ' + stepNum + ' of ' + stepTotal;
    }
  }

  stepBackward() {
    this.updateStepGUI(this.result.stepBackward(), this.result.stepIndex, this.result.timeline.length);
  }


  stepForward() {
    this.updateStepGUI(this.result.stepForward(), this.result.stepIndex, this.result.timeline.length);
  }

  pause() {
    clearInterval(this.interval);
  }

  play() {
    this.interval = setInterval(() => {
      if (this.result.stepIndex < this.result.timeline.length) {
        try {
          this.stepForward();
        } catch (e) {
          this.pause();
          throw e;
        }
      } else {
        this.pause();
      }
    }, this.speed);
  }

}

import Queue from '../util/queue';
// import Stack from '../util/stack';
import { Node } from '../data/node/node';
import { Edge } from '../data/edge/edge';
import AlgorithmResult from '../algorithm/algorithm-result';
import AbstractAlgorithm from '../algorithm/abstract-algorithm';
import StepBuilder from '../algorithm/step-builder';

class TraversalAlgorithm extends AbstractAlgorithm {

  name = 'Traversal';
  // data structure that determines order of traversal
  next = new Queue();

  // starting point for the algorithm
  source = null;

  // things that the GUI needs to supply for the algorithm to run
  inputs = {
    source: {
      type: 'node',
      name: 'Source',
      test: (node) => {
        return node instanceof Node;
      },
      required: true
    }
  };

  result;

  // the members of the nodes and edges that the algorithm will edit
  nodeFields = [
    'color'
  ];
  edgeFields = [
    'color'
  ];

  constructor(graph) {
    super(graph);
    this.result = new AlgorithmResult();
    this.stepBuilder = new StepBuilder(this.nodeFields, this.edgeFields, this.result);
  }

  // visit the specified node
  visitNode(node) {
    // mark node as visited
    this.graphState.set(node, true);

    // add adjacent edges to the queue
    let edges = node.edges;
    for (let edge of edges) {
      if (this.hasVisited(edge) || this.next.has(edge)) {
        continue;
      }
      if (edge.isDirected && edge.startNode === node) {
        this.next.enqueue(edge);
      } else {
        this.next.enqueue(edge);
      }
    }
  }

  // visit the specified edge
  visitEdge(edge) {
    // mark edge as visited
    this.graphState.set(edge, true);

    // add adjacent node to the queue
    if (!this.hasVisited(edge.destNode) && !this.next.has(edge.destNode)) {
      this.next.enqueue(edge.destNode);
    } else if (!this.hasVisited(edge.startNode) && !edge.isDirected && !this.next.has(edge.startNode)) {
      this.next.enqueue(edge.startNode);
    }
  }

  // check if object has been visited by algorithm
  hasVisited(object) {
    return this.graphState.has(object) && this.graphState.get(object);
  }

  // run the next step of the algorithm
  step() {
    if (this.hasStarted && this.next.size === 0) {
      this.isComplete = true;
      return false;
    }

    let nextItem;

    if (this.hasStarted) {
      nextItem = this.next.dequeue();
    } else {
      this.hasStarted = true;
      nextItem = this.source;
    }

    if (nextItem instanceof Node) {
      this.visitNode(nextItem);
    } else if (nextItem instanceof Edge) {
      this.visitEdge(nextItem);
    } else {
      throw Error('Non-graph object in algorithm queue');
    }
    // represent the visual aspects of this step by creating a new step, adding a change for the current object, and completing the step
    this.stepBuilder.newStep(`Visiting ${nextItem.constructor.name} ${nextItem.id}`);
    this.stepBuilder.addChange(nextItem, {
      color: 'black'
    }, {
      color: 'red'
    }, {
      color: 'green'
    });
    this.stepBuilder.completeStep();

    return true;
  }

}

export { TraversalAlgorithm };
export default TraversalAlgorithm;

require('../scss/styles.scss');

import { Graph } from './data/graph';
import ui from './ui/ui';

let graph;

function init() {
  graph = new Graph();
  ui.init(graph);
  ui.toolbar.selectToolByName('node');
  ui.canvas.resize();
  window.requestAnimationFrame(draw);
}

function draw() {
  ui.canvas.clear();
  graph.draw(ui.canvas.context);
  window.requestAnimationFrame(draw);
}

window.addEventListener('load', init, false);

window.addEventListener('resize', (event) => {
  if (ui.canvas) {
    ui.canvas.resize();
  }
}, false);

import { Edge } from './edge';
import { drawArrows } from '../../util/curvedEdge';

export class DashedEdge extends Edge {

  draw(context) {
    context.strokeStyle = this.isSelected ? this.selectedColor : this.color;
    context.lineWidth = this.lineWidth;
    context.setLineDash([ 5, 5 ]);

    // Create a new path
    context.beginPath();

    // Start path at given point
    context.moveTo(this.startPoint.x, this.startPoint.y);

    // Draw line to given point
    context.quadraticCurveTo(this.bezierPoint.x, this.bezierPoint.y, this.destPoint.x, this.destPoint.y);

    // Draw to the canvas
    context.stroke();
    context.setLineDash([]);

    if (this.isDirected) {
      context.fillStyle = this.isSelected ? this.selectedColor : this.color;
      drawArrows(this, false, true);
    }

    if (this.edgeLabel !== '') {
      this.drawLabel(context);
    }
  }

}

import { calcBezierDistance, EDGE_DISTANCE_THRESHOLD } from '../../util/curvedEdge';

export class Edge {

  static numEdges = 0;
  id = Edge.numEdges++;

  // graph data
  startNode;
  destNode;
  isDirected;
  partners = [];

  // status
  isSelected = false;

  // label
  xText;
  yText;
  edgeLabel = '';
  labelFont = '14px Arial';
  labelColor = 'black';
  showTextCtrl = false;

  // appearance
  color = 'black';
  selectedColor = 'red';
  lineWidth = 1;

  constructor(startNode, destNode) {
    let methods = [
      'draw'
    ];

    for (let method of methods) {
      if (typeof this[method] !== 'function') {
        throw TypeError('Must override method: ' + method);
      }
    }

    if (arguments.length < 2) {
      throw Error(`Edge constructor requires at least two arguments: startNode and destNode. Actually passed in ${arguments}`);
    }

    this.startNode = startNode;
    this.destNode = destNode;

    startNode.edges.add(this);
    destNode.edges.add(this);

    // copy partners from an existing partner edge
    for (let edge of this.startNode.edges) {
      if (edge.startNode === this.startNode && edge.destNode === this.destNode) {
        this.partners = edge.partners.slice(0);
        break;
      } else if (edge.destNode === this.startNode && edge.startNode === this.destNode) {
        this.partners = edge.partners.slice(0);
        break;
      }
    }

    // add this edge to partners field of all partner edges
    for (let i = 0; i < this.partners.length; i++) {
      this.partners[i].partners.push(this);
    }
    this.partners.push(this);

    if (this.startNode === this.destNode) {
      this.isDirected = true;
      this.updateEndpoints();
    } else {
      for (let i = 0; i < this.partners.length; i++) {
        this.partners[i].updateEndpoints();
      }
    }

    this.generateDefaultTextLocation();
  }

  detach() {
    // remove this edge from partners of all partner edges
    for (let i = 0; i < this.partners.length; i++) {
      if (this.partners[i] === this) {
        continue;
      }
      let index = this.partners[i].partners.indexOf(this);
      this.partners[i].partners.splice(index, 1);
      this.partners[i].updateEndpoints();
    }

    this.startNode.edges.delete(this);
    this.destNode.edges.delete(this);
    this.startNode = null;
    this.destNode = null;
  }

  updateSelfLoopEndpoints() {
    this.startPoint = this.startNode.getAnglePoint(240);
    this.destPoint = this.startNode.getAnglePoint(300);
    this.bezierPoint = {
      x: (this.startPoint.x + this.destPoint.x) / 2,
      y: this.startPoint.y - 2 * this.startNode.radius
    };
  }

  updateNormalEdgeEndpoints() {
    let dx = this.destNode.x - this.startNode.x;
    let dy = this.destNode.y - this.startNode.y;
    let distance = Math.sqrt(dx * dx + dy * dy);

    // calculate incline based on dy and distance
    // this is the angle between x-axis and the line from startNode to destNode
    let incline = Math.asin(dy / distance);
    // convert from radians to degrees
    incline = incline * 180 / Math.PI;

    // Note that canvas coordinates increase towards the bottom right
    // Quadrants are oriented as follows:
    //       |
    //   Q3  |  Q4
    //       |
    // ------------- +x
    //       |
    //   Q2  |  Q1
    //       |
    //       +y
    //

    // if destNode.x < startNode.x, the angle should end:
    //   in the second quadrant if destNode.y > startNode.y
    //   in the third quadrant if destNode.y < startNode.y
    if (this.startNode.x >= this.destNode.x) {
      if (this.startNode.y >= this.destNode.y) {
        incline = (540 - incline) % 360;
      } else {
        incline = 180 - incline;
      }
    }

    let numPartners = this.partners.length + 1;
    let multiIndex = this.partners.indexOf(this) + 1;

    let ratio = multiIndex / numPartners;

    // if the partner edge with index 0 is going in the opposite direction compared to the current edge
    // then draw the current edge on the opposite side of the line connecting the two nodes
    if (this.partners[0].startNode !== this.startNode) {
      ratio = 1 - ratio;
    }

    let startAngle = incline + 90 * ratio - 45;
    let destAngle = incline + 225 - 90 * ratio;
    this.startPoint = this.startNode.getAnglePoint(startAngle);
    this.destPoint = this.destNode.getAnglePoint(destAngle);

    let orient = ratio - 0.5;
    let diff = {
      x: this.destPoint.x - this.startPoint.x,
      y: this.destPoint.y - this.startPoint.y
    };
    this.bezierPoint = {
      x: (this.startPoint.x + this.destPoint.x) / 2 - orient * diff.y,
      y: (this.startPoint.y + this.destPoint.y) / 2 + orient * diff.x
    };
  }

  updateEndpoints() {
    if (this.startNode === this.destNode) {
      this.updateSelfLoopEndpoints();
    } else {
      this.updateNormalEdgeEndpoints();
    }
    this.updateTextLocation();
  }

  containsPoint(x, y) {
    return EDGE_DISTANCE_THRESHOLD > calcBezierDistance(x, y, this.startPoint.x, this.startPoint.y, this.bezierPoint.x, this.bezierPoint.y, this.destPoint.x, this.destPoint.y);
  }

  draw(context) {
    throw Error('Can\'t call draw from abstract Edge class.');
  }

  drawLabel(context) {
    context.font = this.labelFont;
    context.fillStyle = this.labelColor;
    context.fillText(this.edgeLabel, this.xText, this.yText);
    if (this.showTextCtrl) {
      context.fillStyle = 'red';
      context.beginPath();
      context.arc(this.xText, this.yText, 3.0, 0, 1.5 * Math.PI);
      context.lineTo(this.xText, this.yText);
      context.fill();
    }
  }

  updateTextLocation() {
    this.xText = this.bezierPoint.x;
    this.yText = this.bezierPoint.y;
  }

  // find the starting point of our text box
  generateDefaultTextLocation() {
    // var xOffSet = context.measureText(this.edgeLabel)/2;
    // var yOffSet = 1; //assuming an edge is just 1 pixel

    this.xText = this.bezierPoint.x;
    this.yText = this.bezierPoint.y;
    // console.log("Line xText: " + this.xText + ", yText: " + this.yText);
  }

}

import { Edge } from './edge';
import { drawArrows } from '../../util/curvedEdge';

export class SolidEdge extends Edge {

  draw(context) {
    context.strokeStyle = this.isSelected ? this.selectedColor : this.color;
    context.lineWidth = this.lineWidth;


    // Create a new path
    context.beginPath();

    // Start path at given point
    context.moveTo(this.startPoint.x, this.startPoint.y);

    // Draw line to given point
    context.quadraticCurveTo(this.bezierPoint.x, this.bezierPoint.y, this.destPoint.x, this.destPoint.y);

    // Draw to the canvas
    context.stroke();


    if (this.isDirected) {
      context.fillStyle = this.isSelected ? this.selectedColor : this.color;
      drawArrows(this, false, true);
    }

    if (this.edgeLabel !== '') {
      this.drawLabel(context);
    }
  }

}

export class Graph {
  constructor(nodes, edges) {
    this.nodes = new Set(nodes);
    this.edges = new Set(edges);
  }

  addNode(node) {
    // console.log('Adding node at (' + node.x + ',' + node.y + ')');
    this.nodes.add(node);
  }

  addEdge(edge) {
    if (!this.nodes.has(edge.startNode) || !this.nodes.has(edge.destNode)) {
      throw new Error('Edge nodes are not in the graph');
    }

    // console.log('Adding edge between ' + edge.startNode.id + ' and ' + edge.destNode.id);
    this.edges.add(edge);
  }

  removeNode(node) {
    // console.log('Removing node ' + node.id);
    // Temp copy of edges to work on while we remove them
    let tempEdges = new Set();
    for (let edge of node.edges) {
      tempEdges.add(edge);
    }
    for (let edge of tempEdges) {
      this.removeEdge(edge);
    }
    this.nodes.delete(node);
  }

  removeEdge(edge) {
    // console.log('Removing edge ' + edge.startNode.id + '-' + edge.destNode.id);
    let id = edge.id;
    for (let i = 0; i < edge.partners.length; i++) {
      for (let j = 0; j < edge.partners[i].length; j++) {
        if (edge.partners[i].partners[j].id === id) {
          edge.partners[i].partners.splice(j, 1);
          edge.partners[i].updateEndpoints();
          break;
        }
      }
    }
    this.edges.delete(edge);
    edge.detach();
  }

  hasEdge(start, dest) {
    if (!this.nodes.has(start) || !this.nodes.has(dest)) {
      throw new Error('Nodes are not in the graph');
    }

    for (let edge of this.edges) {
      if (edge.startNode === start && edge.destNode === dest) {
        return true;
      }
    }
    return false;
  }

  hasComponent(x, y, ignore) {
    let component = this.getComponent(x, y);
    return component !== ignore && component !== null;
  }

  getComponent(x, y) {
    let component = null;
    this.forEachNode((node) => {
      if (node.containsPoint(x, y)) {
        component = node;
        return false;
      }
      return true;
    });

    if (component !== null) {
      return component;
    }

    this.forEachEdge((edge) => {
      if (edge.containsPoint(x, y)) {
        component = edge;
        return false;
      }
      return true;
    });

    return component;
  }

  isNodeCollision(testNode, x, y) {
    let collision = false;
    this.forEachNode((node) => {
      if (node === testNode) {
        return true;
      }
      let nodePoint = node.edgePointInDirection(x, y);
      let testPoint = testNode.edgePointInDirection(node.x, node.y);
      if (testNode.containsPoint(nodePoint.x, nodePoint.y)
          || node.containsPoint(testPoint.x, testPoint.y)) {
        collision = true;
        return false;
      }
      return true;
    });
    return collision;
  }

  forEachNode(callback) {
    for (let node of this.nodes) {
      if (callback(node) === false) {
        break;
      }
    }
  }

  forEachEdge(callback) {
    for (let edge of this.edges) {
      if (callback(edge) === false) {
        break;
      }
    }
  }

  draw(context) {
    this.edges.forEach((edge) => {
      edge.draw(context);
    });

    this.nodes.forEach((node) => {
      node.draw(context);
    });
  }
}

import { Node } from './node';

class CircleNode extends Node {

  static radius = 30;

  radius = CircleNode.radius;

  constructor(x, y) {
    super(x, y);
    this.generateDefaultTextLocation();
  }

  containsPoint(x, y) {
    return this.distanceToPoint(x, y) <= this.radius;
  }

  distanceToPoint(x, y) {
    let dx = x - this.x;
    let dy = y - this.y;
    return Math.sqrt(dx * dx + dy * dy);
  }

  edgePointInDirection(x, y) {
    if (x === this.x && y === this.y) {
      throw new Error('Point is at origin of Node');
    }
    let dx = x - this.x;
    let dy = y - this.y;
    let distance = Math.sqrt(dx * dx + dy * dy);
    return {
      x: this.x + dx * this.radius / distance,
      y: this.y + dy * this.radius / distance
    };
  }

  draw(context) {
    // let xOffSet = 0;
    // let yOffset = 0;
    context.fillStyle = this.fillColor;
    context.strokeStyle = this.isSelected ? this.selectedColor : this.color;
    context.lineWidth = this.lineWidth;

    // Create a new path
    context.beginPath();

    // Create an arc with center at (x, y)
    context.arc(this.x, this.y, this.radius, 0, 2 * Math.PI);
    // Draw to the canvas
    context.fill();
    context.stroke();

    if (this.nodeLabel !== '') {
      this.drawLabel(context);
    }

    if (this.isAcceptingState) {
      this.drawAcceptingState(context);
    }

    if (this.isStartingState) {
      this.drawStartingState(context);
    }
  }

  drawLabel(context) {
    // xOffSet = context.measureText(this.nodeLabel)/2;
    context.font = this.labelFont;
    context.fillStyle = this.labelColor;
    context.fillText(this.nodeLabel, this.xText, this.yText);
    if (this.showTextCtrl) {
      context.fillStyle = 'red';
      context.beginPath();
      context.arc(this.xText, this.yText, 3.0, 0, 1.5 * Math.PI);
      context.lineTo(this.xText, this.yText);
      context.fill();
    }
  }

  drawAcceptingState(context) {
    context.fillStyle = this.fillColor;
    context.strokeStyle = this.isSelected ? this.selectedColor : this.color;
    context.moveTo(this.x + this.radius * 0.75, this.y);
    context.arc(this.x, this.y, this.radius * 0.75, 0, 2 * Math.PI);
    context.fill();
    context.stroke();
  }

  drawStartingState(context) {
    let endpoint = this.getAnglePoint(225);
    context.fillStyle = this.isSelected ? this.selectedColor : this.color;
    context.strokeStyle = this.isSelected ? this.selectedColor : this.color;
    context.moveTo(endpoint.x - 30, endpoint.y - 30);
    context.lineTo(endpoint.x, endpoint.y);
    context.stroke();
    context.beginPath();
    context.moveTo(endpoint.x, endpoint.y);
    context.lineTo(endpoint.x - 6 - 3, endpoint.y - 6 + 3);
    context.lineTo(endpoint.x - 6, endpoint.y - 6);
    context.lineTo(endpoint.x - 6 + 3, endpoint.y - 6 - 3);
    context.closePath();
    context.fill();
  }

  /*
