      this.yText = this.y -(this.radius + 4 + (context.measureText(this.nodeLabel)/2));
    } else if() {
      // place at top-left
      this.xText = this.x -(this.radius + 4 + (context.measureText(this.nodeLabel)/2));
      this.yText = this.y + this.radius + 4 + (context.measureText(this.nodeLabel)/2);
