    if (this.selectedObject) {
      this.selectedObject.isSelected = false;
    }
    obj.isSelected = true;
    this.selectedObject = obj;
  }

  // drag: multiselect?

  selectNone(event, graph, x, y) {
    if (this.selectedObject) {
      this.selectedObject.isSelected = false;
      this.selectedObject = null;
    }
  }

}

export class Tool {

  name; // displayed name
  icon; // icon to display
  sidebarType = 'display';

  constructor(icon) {
    this.icon = icon;
