    return typeof this.currentMode !== 'undefined'
        && typeof this.constructor.modes !== 'undefined'
        && typeof this.optionMap !== 'undefined'
        && typeof this.optionContent !== 'undefined';
  }

  hasInputs() {
    return typeof this.inputs !== 'undefined'
        && typeof this.inputTypes !== 'undefined';
  }

  changeMode(mode) {
    this.currentMode = mode;
  }

  // These is called when changing tools
  // Set up the interface
  activate() {}
  // Undo any incomplete actions
  cancel() {}

  // mouse events on graph components
  preSelectObject(event, graph, obj, x, y) {
    return true;
  }
  preDragObject(event, graph, srcObj, x, y) {
    return true;
  }
  selectObject(event, graph, obj, x, y) {}
  dragObject(event, graph, srcObj, startX, startY, x, y) {}
  dropOnObject(event, graph, droppedObj, destObj, startX, startY, x, y) {}
  dragOverObject(event, graph, obj, startX, startY, x, y) {
    return this.dragNone(event, graph, startX, startY, x, y);
  }

  // mouse events on empty space
  abortSelect(event, graph, x, y) {}
  preSelectNone(event, graph, x, y) {}
  preDragNone(event, graph, x, y) {}
  selectNone(event, graph, x, y) {}
  dragNone(event, graph, startX, startY, x, y) {}
  dropOnNone(event, graph, droppedObj, startX, startY, x, y) {}

}

import Stepper from '../algorithm/stepper';
import ui from '../ui/ui';

let stepper = new Stepper();
let graph = null;
let inputs;
let curAlgorithm;

export function init(g) {
  graph = g;
}

export function selectObject(obj) {
  let sidebarContent = ui.sidebar.content;
  let currentInput = sidebarContent.getCurrentInput();

  if (currentInput && inputs && inputs[currentInput].test(obj)) {
    sidebarContent.updateInput(currentInput, obj);
  }
}

export function setAlgorithm(AlgorithmClass) {
  curAlgorithm = new AlgorithmClass(graph);
  inputs = curAlgorithm.inputs;

  stepper.reset();

  let sidebarContent = ui.sidebar.content;
  sidebarContent.updateAlgorithm(curAlgorithm);
}

export function getAlgorithmInputs() {
  return inputs;
}

export function setInputValues(inputData) {
  for (let name of Object.keys(inputData)) {
    if (name in inputs) {
      let value = inputData[name];
      curAlgorithm[name] = value;
    }
  }
}

export function run() {
  let hasNextStep = true;
  while (hasNextStep) {
    hasNextStep = curAlgorithm.step();
  }
  stepper.setResult(curAlgorithm.getResult());
}

export function play() {
  if (stepper.result === null) {
    return;
  }
  stepper.play();
}

export function pause() {
  stepper.pause();
}

export function viewNext() {
  stepper.stepForward();
}

export function viewPrevious() {
  stepper.stepBackward();
}

import { initCurved } from '../util/curvedEdge';
import MouseHandler from '../util/mouse-handler';
import ContextMenu from '../ui/context-menu';

const SCALE_MODIFIER = 0.9;

class Canvas {

  ui;

  canvas;
  context;

  // scale of 0.5 means drawing at half size (0.5 pixels for each original pixel)
  scaleValue = 1;
  dx = 0;
  dy = 0;

  mouseHandler;
  contextMenu;

  constructor(ui) {
    this.ui = ui;
    this.canvas = document.getElementById('canvas');
    this.context = this.canvas.getContext('2d');
    initCurved(this.canvas, this.context);
  }

  init(graph) {
    this.mouseHandler = new MouseHandler(graph);
    this.contextMenu = new ContextMenu(this.ui, this.mouseHandler);
    this.initListeners();
  }

  resize(event) {
    this.context.canvas.width = window.innerWidth;
    this.context.canvas.height = window.innerHeight;
    this.update();
  }

  get scale() {
    return this.scaleValue;
  }

  set scale(scale) {
    this.scaleValue = scale;
    this.ui.statusBar.updateZoom(scale);
  }

  reset() {
    this.dx = 0;
    this.dy = 0;
    this.scale = 1;
    this.update();
    this.ui.statusBar.updateMouse(0, 0);
  }

  clear() {
    this.context.clearRect(this.dx, this.dy, this.canvas.width / this.scale, this.canvas.height / this.scale);
  }

  getCanvasX(event) {
    let canvasX = event.offsetX;
    let x = canvasX / this.scale + this.dx;
    return x;
  }

  getCanvasY(event) {
    let canvasY = event.offsetY;
    let y = canvasY / this.scale + this.dy;
    return y;
  }

  update() {
    // reset the transformations done to the canvas
    this.context.resetTransform();

    // context.setTransform(xScale, xSkew, ySkew, yScale, dx, dy) applies
    // the translation before the scale.
    // This is not what we want, since the scale is done relative to (0,0)
    // and the position displayed at the top-left corner will no longer
    // be (dx,dy), which means the coordinates under the mouse will change.

    // scale both x- and y-axis
    this.context.scale(this.scale, this.scale);

    // context.translate(a, b) translates the canvas origin by (a,b).
    // Since (dx,dy) are the coordinates calculated for the new origin, the
    // canvas needs to be translated by (-dx,-dy).
    this.context.translate(-1 * this.dx, -1 * this.dy);
  }

  initListeners() {
    this.canvas.addEventListener('mousedown', (event) => {
      let x = this.getCanvasX(event);
      let y = this.getCanvasY(event);

      if (this.contextMenu.menuState === 0 && event.button !== 2) {
        this.mouseHandler.downListener(event, this.ui.toolbar.currentTool, x, y);
      }
    }, false);

    this.canvas.addEventListener('mouseup', (event) => {
      let x = this.getCanvasX(event);
      let y = this.getCanvasY(event);

      if (this.contextMenu.menuState === 1) {
        this.contextMenu.toggleContextMenu();
      } else if (this.contextMenu.menuState === 0 && event.button !== 2) {
        this.mouseHandler.upListener(event, this.ui.toolbar.currentTool, x, y);
      }
    }, false);

    this.canvas.addEventListener('mousemove', (event) => {
      let x = this.getCanvasX(event);
      let y = this.getCanvasY(event);

      this.mouseHandler.moveListener(event, this.ui.toolbar.currentTool, x, y);
    }, false);

    this.canvas.addEventListener('contextmenu', (event) => {
      this.contextMenu.contextmenuEventListener(event, this.getCanvasX(event), this.getCanvasY(event));
    }, false);

    this.canvas.addEventListener('wheel', (event) => {
      // prevent page scrolling (the default scroll behavior)
      event.preventDefault();

      // store the current scale value
      let oldScale = this.scale;

      // get the amount the mousewheel was scrolled
      let delta = event.deltaY;
      if (delta > 0) {
        // scroll down
        this.scale = Math.max(this.scale * SCALE_MODIFIER, 0.1);
      } else if (delta < 0) {
        // scroll up
        this.scale = Math.min(this.scale / SCALE_MODIFIER, 10);
      }

      // get the mouse position (relative to the canvas element)
      let mouseX = event.offsetX;
      let mouseY = event.offsetY;

      // Calculate the new position of the displayed origin (top left corner).
      // (dx,dy) are the coordinates of the top-left corner.
      // (x1,y1) and (x2,y2) are the coordinates at the mouse position before
      // and after changing the scale, respectively.
      //
      // With the old scale value:
      //     dx1 = dx
      //     dy1 = dy
      //     x1 = dx + mouseX / oldScale
      //     y1 = dy + mouseY / oldScale
      //
      // With new scale:
      //     dx2 = dx + mouseX / oldScale - mouseX / scale
      //     dy2 = dy + mouseY / oldScale - mouseY / scale
      //     x2 = dx2 + mouseX / scale
      //        = dx + mouseX / oldScale - mouseX / scale + mouseX / scale
      //        = dx + mouseX / oldScale
      //        = x1
      //     y2 = dy2 + mouseY / scale
      //        = dy + mouseY / oldScale - mouseY / scale + mouseY / scale
      //        = dy + mouseY / oldScale
      //        = y1
      //
      // Note that (x1,y1) = (x2,y2) which means the coordinates under
      // the mouse stays the same.
      this.dx += mouseX / oldScale - mouseX / this.scale;
      this.dy += mouseY / oldScale - mouseY / this.scale;

      this.update();

      this.ui.statusBar.updateZoom(this.scale);
    }, false);
  }
}

export { Canvas };
export default Canvas;

import { Node } from 'data/node/node';
import { Edge } from 'data/edge/edge';

class ContextMenu {

  ui;

  mouseHandler;

  contextMenu;

  menuState = 0;
  menuPosX;
  menuPosY;
  component;
  taskArg = {
    'Add Circle Node': 'circle',
    'Add Square Node': 'square',
    'Toggle Accepting State': 'isAcceptingState',
    'Toggle Start State': 'isStartingState',
    'Toggle Directed Edge': 'isDirected',
    'Delete Node': 'node',
    'Delete Edge': 'edge'
  };

  constructor(ui, mouseHandler) {
    this.ui = ui;
    this.mouseHandler = mouseHandler;
    this.contextMenu = document.getElementById('context-menu');
    this.initListeners();
  }

  initListeners() {
    this.contextMenu.addEventListener('mouseup', (event) => {
      let task = event.srcElement.innerText;
      let type = task.split(' ')[0];

      if (type === 'Add') {
        this.mouseHandler.contextAdd(this.taskArg[task], this.menuPosX, this.menuPosY);
      } else if (type === 'Toggle') {
        this.mouseHandler.contextToggle(this.taskArg[task], this.component);
      } else if (type === 'Delete') {
        this.mouseHandler.contextDelete(this.taskArg[task], this.component);
      } else if (type === 'Edit') {
        this.ui.toolbar.selectToolByName('metadata');
        this.mouseHandler.contextSelect(event, this.ui.toolbar.currentTool, this.component, this.menuPosX, this.menuPosY);
      }

      this.toggleContextMenu();
    }, false);
  }

  classDisplayChange(className, displayType) {
    let items = this.contextMenu.getElementsByClassName(className);
    for (let i = 0; i < items.length; ++i) {
      items[i].style.display = displayType;
    }
  }

  toggleContextMenu() {
    if (this.menuState !== 1) {
      this.contextMenu.style.display = 'block';
      this.menuState = 1;
    } else {
      this.menuState = 0;
      this.contextMenu.style.display = 'none';
      this.classDisplayChange('component', 'none');
      this.classDisplayChange('component-node', 'none');
      this.classDisplayChange('component-edge', 'none');
      this.classDisplayChange('blank', 'none');
    }
  }

  repositionMenu(event) {
    let xpos = event.pageX;
    let ypos = event.pageY;

    document.getElementById('context-menu').style.left = xpos + 'px';
    document.getElementById('context-menu').style.top = ypos + 'px';
  }

  contextmenuEventListener(event, x, y) {
    // prevent default context menu
    event.preventDefault();

    this.menuPosX = x;
    this.menuPosY = y;

    this.component = this.mouseHandler.contextComponent(event, this.menuPosX, this.menuPosY);

    this.toggleContextMenu();

    this.repositionMenu(event);

    if (this.component !== null) {
      this.classDisplayChange('component', 'block');
      if (this.component instanceof Node) {
        this.classDisplayChange('component-node', 'block');
      } else if (this.component instanceof Edge) {
        this.classDisplayChange('component-edge', 'block');
      }
    } else {
      this.classDisplayChange('blank', 'block');
    }
  }

}

export { ContextMenu };
export default ContextMenu;

const DROPDOWN_CLASS = 'dropdown';
const DROPDOWN_HIDDEN_CLASS = 'dropdown--hidden';
const DROPDOWN_TOGGLE_CLASS = 'dropdown--active';

const DROPDOWN_SELECT_CLASS = 'dropdown__select';

const DROPDOWN_DISPLAY_CLASS = 'dropdown__select__display';

const DROPDOWN_MENU_CLASS = 'dropdown__menu';
const DROPDOWN_MENU_TOGGLE_CLASS = 'dropdown__menu--active';

const DROPDOWN_OPTIONLIST_CLASS = 'dropdown__menu__list';

const DROPDOWN_OPTION_CLASS = 'dropdown__menu__list__item';
const DROPDOWN_OPTION_SELECTED_CLASS = 'dropdown__menu__list__item--selected';
const DROPDOWN_OPTION_INDEX_ATTRIBUTE = 'data-index';
const DROPDOWN_OPTION_VALUE_ATTRIBUTE = 'data-value';
const DROPDOWN_OPTION_LABEL_ATTRIBUTE = 'data-label';

class Dropdown {

  // // width of options
  // width;
  //
  // // height of options
  // height;
  //
  // // number of elements to display in a row in the dropdown menu
  // numCols;
  //
  // // number of rows to display in the dropdown menu (before needing a scrollbar)
  // numRows;

  selectElement;
  optionMap;
  optionContent;

  // container element that is a common ancestor for all other dropdown elements
  container;

  // the element that displays the selected option
  display;

  // dropdown menu that contains all the options
  menu;

  // list of options
  optionlist;

  isDisplayingMenu = false;

  selectedOption = null;

  constructor(selectElement) {
    this.selectElement = selectElement;
    this.createDropdown();
  }

  get value() {
    if (!this.selectOption) {
      throw Error('No option is selected');
    }
    return this.selectElement.options[this.selectOption.getAttribute(DROPDOWN_OPTION_INDEX_ATTRIBUTE)];
  }

  getOptionsHtml() {
    let options = this.selectElement.children;
    let optionsHtml = '';
    for (let i = 0; i < options.length; i++) {
      let option = options[i];
      optionsHtml += `
        <li class="${DROPDOWN_OPTION_CLASS} ${this.selectElement.value === option.value ? DROPDOWN_OPTION_SELECTED_CLASS : ''}"
            ${DROPDOWN_OPTION_INDEX_ATTRIBUTE}="${option.index}"
            ${DROPDOWN_OPTION_VALUE_ATTRIBUTE}="${option.value}"
            ${DROPDOWN_OPTION_LABEL_ATTRIBUTE}="${this.optionMap[option.text].label}">
          ${this.optionContent.html}
        </li>`;
    }
    return optionsHtml;
  }

  createDropdown() {
    let optionsHtml = this.getOptionsHtml();
    let dropdownHtml = `
      <div class="${DROPDOWN_CLASS}">
        <div class="${DROPDOWN_SELECT_CLASS}">
          <div class="${DROPDOWN_DISPLAY_CLASS}"></div>
          <div class="dropdown__select__arrow">
            <span></span>
          </div>
        </div>
        <div class="${DROPDOWN_MENU_CLASS}">
          <ul class="${DROPDOWN_OPTIONLIST_CLASS}">
            ${optionsHtml}
          </ul>
        </div>
      </div>
    `;

    let ancestor = this.selectElement.parentNode;

    let div = document.createElement('div');
    div.innerHTML = dropdownHtml;
    ancestor.appendChild(div.children[0]);

    this.container = ancestor.querySelector(`.${DROPDOWN_CLASS}`);
    this.display = ancestor.querySelector(`.${DROPDOWN_DISPLAY_CLASS}`);
    this.menu = ancestor.querySelector(`.${DROPDOWN_MENU_CLASS}`);
    this.optionlist = ancestor.querySelector(`.${DROPDOWN_OPTIONLIST_CLASS}`);

    this.updateOptionContent();

    this.initListeners();
  }

  updateOptionContent() {
    let options = this.optionlist.querySelectorAll(`.${DROPDOWN_OPTION_CLASS}`);
    for (let i = 0; i < options.length; i++) {
      let option = options[i];
      if (option === this.selectedOption) {
        this.optionContent.init(this.display, this.selectedOption.getAttribute(DROPDOWN_OPTION_VALUE_ATTRIBUTE));
      }
      this.optionContent.init(option, option.getAttribute(DROPDOWN_OPTION_VALUE_ATTRIBUTE));
    }
  }

  initListeners() {
    document.addEventListener('click', (event) => {
      if (!this.container.contains(event.target)) {
        this.hideMenu();
      }
    });

    this.container.addEventListener('click', (event) => {
      this.toggleMenu();
    });
    this.optionlist.addEventListener('click', (event) => {
      if (event.target.classList.contains(DROPDOWN_OPTION_CLASS)) {
        this.selectOption(event.target);
      }
    });

    // Create MutationObserver to check for changes in selectElement options
    let obs = new MutationObserver((mutations, observer) => {
      for (let mutation of mutations) {
        if (mutation.addedNodes || mutation.removedNodes) {
          this.updateOptions();
          break;
        }
      }
    });
    // Observe document
    obs.observe(this.selectElement, {
      childList: true,
      attributes: true,
      characterData: true,
      subtree: true
    });
  }

  updateOptions() {
    this.optionlist.innerHTML = this.getOptionsHtml();

    if (this.optionlist.children.length === 0) {
      this.container.classList.add(DROPDOWN_HIDDEN_CLASS);
    } else {
      this.container.classList.remove(DROPDOWN_HIDDEN_CLASS);
    }

    let index = this.selectElement.selectedIndex;
    this.selectedOption = this.optionlist.querySelector(`.${DROPDOWN_OPTION_CLASS}[${DROPDOWN_OPTION_INDEX_ATTRIBUTE}="${index}"]`);
    if (this.selectedOption) {
      this.selectedOption.classList.add(DROPDOWN_OPTION_SELECTED_CLASS);
    }
    this.displaySelectedOption();
    this.updateOptionContent();
  }

  toggleMenu() {
    if (this.isDisplayingMenu) {
      this.hideMenu();
    } else {
      this.showMenu();
    }
  }

  showMenu() {
    this.container.classList.add(DROPDOWN_TOGGLE_CLASS);
    this.menu.classList.add(DROPDOWN_MENU_TOGGLE_CLASS);
    this.isDisplayingMenu = true;
  }

  hideMenu() {
    this.container.classList.remove(DROPDOWN_TOGGLE_CLASS);
    this.menu.classList.remove(DROPDOWN_MENU_TOGGLE_CLASS);
    this.isDisplayingMenu = false;
  }

  selectOption(optionElement) {
    if (this.selectedOption) {
      this.selectedOption.classList.remove(DROPDOWN_OPTION_SELECTED_CLASS);
    }

    this.selectedOption = optionElement;
    this.selectedOption.classList.add(DROPDOWN_OPTION_SELECTED_CLASS);

    this.selectElement.options[this.selectElement.selectedIndex].selected = false;
    this.selectElement.selectedIndex = this.selectedOption.getAttribute(DROPDOWN_OPTION_INDEX_ATTRIBUTE);
    this.selectElement.options[this.selectElement.selectedIndex].selected = true;

    // Dispatch the 'change' event since modifying selectedIndex doesn't do it automatically
    let event = new Event('change');
    this.selectElement.dispatchEvent(event);

    this.displaySelectedOption();
  }

  displaySelectedOption() {
    if (this.selectedOption) {
      this.display.innerHTML = this.selectedOption.innerHTML;

      let srcCanvas = this.selectedOption.querySelector('canvas');
      let destCanvas = this.display.querySelector('canvas');
      if (srcCanvas && destCanvas) {
        let destCtx = destCanvas.getContext('2d');
        destCtx.clearRect(0, 0, destCanvas.width, destCanvas.height);
        destCtx.drawImage(srcCanvas, 0, 0);
      }
    } else {
      this.display.innerHTML = '';
    }
  }
}

export { Dropdown };
export default Dropdown;

export function init() {
  initListeners();
}

export function initListeners() {
  // Create MutationObserver to check for changes in selectElement options
  let obs = new MutationObserver((mutations, observer) => {
    for (let mutation of mutations) {
      if (mutation.addedNodes) {
        for (let element of mutation.addedNodes) {
          // Check if input was added
          if (element.tagName === 'FIELDSET') {
            let inputs = element.querySelectorAll('input, textarea');
            for (let i = 0; i < inputs.length; i++) {
              let input = inputs[i];
              if (input.value === '') {
                input.classList.add('empty');
              } else {
                input.classList.remove('empty');
              }
            }
          }
        }
      }
    }
  });

  // Observe document for element addition/removal
  obs.observe(document, {
    childList: true,
    subtree: true
  });

  document.addEventListener('change', (event) => {
    checkInputEmpty(event);
  });

  document.addEventListener('input', (event) => {
    checkInputEmpty(event);
  });

  document.addEventListener('click', (event) => {
    checkLabelClick(event);
  });
}

function checkInputEmpty(event) {
  if (event.target.tagName === 'INPUT' || event.target.tagName === 'TEXTAREA') {
    let input = event.target;
    if (!input.validity.valid) {
      return;
    }
    if (input.value === '') {
      input.classList.add('empty');
    } else {
      input.classList.remove('empty');
    }
  }
}

function checkLabelClick(event) {
  if (event.target.tagName === 'LABEL') {
    let input = event.target.parentNode.querySelector('input, select, textarea');
    let type = input.type;

    if (type === 'checkbox' || type === 'radio' || type === 'color') {
      input.click();
    } else {
      input.focus();
    }
  }
}

export function createForm(fields) {
  let html = '';

  for (let field of fields) {
    let fieldHtml;

    let type = field.type;
    let name = field.name;
    let value = field.value;
    if (type === 'number') {
      if (value !== Math.round(value)) {
        value = value.toFixed(2);
      }
      fieldHtml = `<input type="number" name="${name}" value="${value}" step="any">`;
    } else if (type === 'boolean') {
      fieldHtml = `<input type="checkbox" name="${name}" ${value ? 'checked="true"' : ''}>`;
    } else if (type === 'string') {
      fieldHtml = `<input type="text" name="${name}" value="${value}">`;
    } else if (type === 'color') {
      fieldHtml = `<input type="color" name="${name}" value="${value}">`;
    } else {
      fieldHtml = `<output>${value}</output>`;
    }

    let displayName = field.displayName;
    html += `
      <fieldset>
        ${fieldHtml}
        <label>${displayName}</label>
      </fieldset>`;
  }

  return html;
}

export function getData(form, graph) {
  let data = {};
  let fieldsets = form.querySelectorAll('fieldset');

  for (let i = 0; i < fieldsets.length; i++) {
    let inputs = fieldsets[i].querySelectorAll('input, select, textarea');
    let fieldData = getFieldData(inputs, graph);
    for (let field of Object.keys(fieldData)) {
      data[field] = fieldData[field];
    }
  }
  return data;
}

export function displayError(form, field, showError) {
  let fieldset = form.querySelectorAll(`fieldset[name="${field}"]`);
  if (showError) {
    fieldset[0].classList.add('error');
  } else {
    fieldset[0].classList.remove('error');
  }
}

function getFieldData(inputs, graph) {
  let data = {};
  for (let i = 0; i < inputs.length; i++) {
    let input = inputs[i];
    let name = input.name;
    let value;
    if (input.tagName === 'INPUT') {
      value = getInputValue(input, graph);
    }
    data[name] = value;
  }
  return data;
}

function getInputValue(input, graph) {
  let value;
  let type = input.getAttribute('type');
  let dataType = input.getAttribute('data-type');
  if (type === 'checkbox') {
    value = input.checked;
  } else if (type === 'number') {
    value = parseInt(input.value, 10);
  } else if (type === 'hidden' && [ 'node', 'edge' ].indexOf(dataType) >= 0) {
    let id = parseInt(input.value, 10);
    if (dataType === 'node') {
      value = getNodeById(graph, id);
    } else if (dataType === 'edge') {
      value = getEdgeById(graph, id);
    }
  } else {
    value = input.value;
  }
  return value;
}

function getNodeById(graph, id) {
  let value = null;
  graph.forEachNode((node) => {
    if (node.id === id) {
      value = node;
      return false;
    }
    return true;
  });
  return value;
}

function getEdgeById(graph, id) {
  let value = null;
  graph.forEachEdge((edge) => {
    if (edge.id === id) {
      value = edge;
      return false;
    }
    return true;
  });
  return value;
}

import SidebarContent from '../ui/sidebar-content';
import { Node } from '../data/node/node';
import { Edge } from '../data/edge/edge';
import * as Form from '../ui/form';
import * as AlgorithmInterface from '../ui/algorithm';

class SidebarAlgorithm extends SidebarContent {

  currentInput;

  constructor(graph) {
    super(graph);

    document.getElementById('sidebar').addEventListener('click', (event) => {
      if (event.target.classList.contains('run-algorithm-btn')) {
        let form = event.target.parentNode;
        let data = Form.getData(form, this.graph);

        let hasError = false;
        let inputs = AlgorithmInterface.getAlgorithmInputs();
        for (let inputName of Object.keys(inputs)) {
          let showError = !inputs[inputName].test(data[inputName]);
          Form.displayError(form, inputName, showError);

          if (showError && !hasError) {
            hasError = true;
          }
        }

        if (hasError) {
          return;
        }
        AlgorithmInterface.setInputValues(data);
        AlgorithmInterface.run();
      } else if (event.target.classList.contains('data-select-btn')) {
        let output = event.target.previousElementSibling;
        let input = output.previousElementSibling;

        let inputName = input.name;
        if (this.currentInput === inputName) {
          this.currentInput = null;
          if (input.value) {
            event.target.textContent = `Change ${input.getAttribute('data-type')}`;
          } else {
            event.target.textContent = `Select ${input.getAttribute('data-type')}`;
          }
        } else {
          this.currentInput = inputName;
          event.target.textContent = 'Cancel';
        }
      } else if (event.target.classList.contains('algorithm-next-btn')) {
        AlgorithmInterface.viewNext();
      } else if (event.target.classList.contains('algorithm-prev-btn')) {
        AlgorithmInterface.viewPrevious();
      } else if (event.target.classList.contains('algorithm-play-btn')) {
        AlgorithmInterface.play();
      } else if (event.target.classList.contains('algorithm-pause-btn')) {
        AlgorithmInterface.pause();
      }
    });

    document.getElementById('sidebar').addEventListener('mouseover', (event) => {
      if (event.target.classList.contains('graph-link')) {
        let type = event.target.getAttribute('data-type');
        let id = event.target.getAttribute('data-id');
        this.toggleHover(type, parseInt(id, 10), true);
      }
    });

    document.getElementById('sidebar').addEventListener('mouseout', (event) => {
      if (event.target.classList.contains('graph-link')) {
        let type = event.target.getAttribute('data-type');
        let id = event.target.getAttribute('data-id');
        this.toggleHover(type, parseInt(id, 10), false);
      }
    });
  }

  toggleHover(type, id, isHovering) {
    if (type === 'node') {
      this.graph.forEachNode((node) => {
        if (node.id === id) {
          node.isSelected = isHovering;
        }
      });
    } else if (type === 'edge') {
      this.graph.forEachEdge((edge) => {
        if (edge.id === id) {
          edge.isSelected = isHovering;
        }
      });
    }
  }

  display() {
    this.tabs.replaceTabs({
      algorithm: 'Algorithm'
    });

    this.tabs.setTabContent('algorithm', '<form></form>');

    this.update();
    this.tabs.selectTab('algorithm');
  }

  updateAlgorithm(algorithm) {
    let form = this.createForm(algorithm.inputs);
    let html = `
      ${form}
      <button type="button" class="run-algorithm-btn">Generate results</button>
      <div>
        <button type="button" class="algorithm-prev-btn">Previous step</button>
        <button type="button" class="algorithm-next-btn">Next step</button>
      </div>
      <div>
        <button type="button" class="algorithm-play-btn">Play</button>
        <button type="button" class="algorithm-pause-btn">Pause</button>
      <div>
      <div>
        <p class="algorithm-step-num">Step #</p>
        <p class="algorithm-step-des">This step is...</p>
      </div>
    `;
    this.tabs.getTabContentElement('algorithm').querySelector('form').innerHTML = html;
    this.tabs.setTabContent('algorithm', html);
  }

  createForm(inputs) {
    let html = '';

    for (let inputName of Object.keys(inputs)) {
      let fieldHtml;

      let input = inputs[inputName];

      let type = input.type;
      let name = inputName;
      let isRequired = input.required;

      if (type === 'number') {
        fieldHtml = `<input type="number" name="${name}" class="${isRequired ? 'required' : ''}">`;
      } else if (type === 'boolean') {
        fieldHtml = `<input type="checkbox" name="${name}" class="${isRequired ? 'required' : ''}">`;
      } else if (type === 'string') {
        fieldHtml = `<input type="text" name="${name}" class="${isRequired ? 'required' : ''}">`;
      } else if (type === 'color') {
        fieldHtml = `<input type="color" name="${name}" class="${isRequired ? 'required' : ''}">`;
      } else if (type === 'node' || type === 'edge') {
        fieldHtml = `
          <input type="hidden" name="${name}" data-type="${type}" class="${isRequired ? 'required' : ''}">
          <output name="${name}"></output>
          <button type="button" class="data-select-btn">Choose ${type}</button>
        `;
      }

      let displayName = input.name;
      html += `
        <fieldset class="${isRequired ? 'required' : ''}" name="${name}">
          <label>${displayName}</label>
          ${fieldHtml}
        </fieldset>
      `;
    }

    return html;
  }

  getCurrentInput() {
    return this.currentInput;
  }

  updateInput(name, obj) {
    // update displayed input values
    if (name === this.currentInput) {
      let id = obj.id;
      let sidebar = document.getElementById('sidebar');

      let input = sidebar.querySelector(`input[name="${name}"]`);
      input.value = id;

      let output = sidebar.querySelector(`output[name="${name}"]`);
      output.value = `Node ${id}`;

      let button = output.nextElementSibling;
      button.textContent = `Change ${input.getAttribute('data-type')}`;

      this.currentInput = null;
    }
  }

  createLinkElement(obj) {
    let id = obj.id;
    let name;

    let type;
    if (obj instanceof Node) {
      type = 'node';
      name = `Node ${id}`;
    } else if (obj instanceof Edge) {
      type = 'edge';
      name = `Edge ${id}`;
    }

    return `
      <div class="graph-link" data-type="${type}" data-id="${id}">${name}</div>
    `;
  }

  update() {}
}

