// import Queue from '../util/queue';
// import Stack from '../util/stack';
// import { Node } from '../data/node/node';
// import { Edge } from '../data/edge/edge';

/**
 * Abstract class for algorithms.
 * @class AbstractAlgorithm
 */
class AbstractAlgorithm {

  /**
   * Name of the algorithm.
   * @type {string}
   */
  name;
  /**
   * Reference to the graph the algorithm is running on.
   * @type {Graph}
   */
  graph;

  /**
   * Data structure that determines order of traversal.
   * @type {Stack|Queue}
   */
  next;

  /**
   * Map that stores algorithm values.
   * @type {WeakMap}
   */
  graphState = new WeakMap();

  /**
   * Whether the algorithm is complete or not.
   * @type {boolean}
   */
  isComplete = false;

  /**
   * Whether the algorithm has started or not.
   * @type {boolean}
   */
  hasStarted = false;

  /**
   * @typedef InputType
   * @type {object}
   * @property {string} type - String representing tyoe of input.
   * @property {string} name - Name to be displayed.
   * @property {method} test - Function run to validate input.
   * @property {boolean} required - Whether or not the input is required.
   */

  /**
   * Object that defines the algorithm input types.
   * @type {Object.<string, InputType>}
   */
  inputs = {};

  result;

  nodeFields = [];
  edgeFields = [];

  stepBuilder;

  constructor(graph) {
    this.graph = graph;
  }

  getResult() {
    return this.result;
  }

  // run the next step of the algorithm
  step() {}

}

export { AbstractAlgorithm };
export default AbstractAlgorithm;

// import { Node } from '../data/node/node';
// import { Edge } from '../data/edge/edge';
// import Step from '../algorithm/step';

/**
 * Stores the results generated by running an Algorithm. An instance will be stored in each Algorithm object.
 * @class AlgorithmResult
 */
class AlgorithmResult {

  /**
   * Array of Step objects.
   * @type {Array.<Step>}
   */
  timeline;

  /**
   * Current index in the timeline.
   * @type {number}
   */
  stepIndex;

  /**
   * Constructs an instance of AlgorithmResult.
   * @param  {Array.<string>} nodeFields - Array of fields to store changes for in Node objects.
   * @param  {Array.<string>} edgeFields - Array of fields to store changes for in Edge objects.
   * @constructs AlgorithmResult
   */
  constructor() {
    this.timeline = [];
    this.stepIndex = -1;
  }

  stepForward() {
    if (this.stepIndex === this.timeline.length) {
      return 'Algorithm finished!';
    }
    if (this.stepIndex > -1) {
      let finishingStep = this.timeline[this.stepIndex];
      finishingStep.applyPost();
    }
    if (this.stepIndex < this.timeline.length - 1) {
      let nextStep = this.timeline[this.stepIndex + 1];
      nextStep.applyDuring();
      this.stepIndex++;
      return nextStep.description;
    }
    this.stepIndex++;
    return 'Done!';
  }

  stepBackward() {
    if (this.stepIndex === -1) {
      return 'Algorithm ready to begin!';
    }
    if (this.stepIndex < this.timeline.length) {
      let redactingStep = this.timeline[this.stepIndex];
      redactingStep.applyPre();
    }
    if (this.stepIndex > 0) {
      let previousStep = this.timeline[this.stepIndex - 1];
      previousStep.applyDuring();
      this.stepIndex--;
      return previousStep.description;
    }
    this.stepIndex--;
    return 'Ready!';
  }

  /**
   * Add a Step to the AlgorithmResult.
   * @param {Step} step - The step to add.
   */
  addStep(step) {
    this.timeline.push(step);
  }
}

export { AlgorithmResult };
export default AlgorithmResult;

/**
 * Represents changes for a Node or Edge during a step of the algorithm.
 * @class Change
 */
class Change {

  /**
   * Object that the Change is storing values for.
   * @type {(Node|Edge)}
   */
  object;

  /**
   * Object containing the values before the step.
   * @type {Object.<string, *>}
   */
  preStepValues;

  /**
   * Object containing the values during the step.
   * @type {Object.<string, *>}
   */
  duringStepValues;

  /**
   * Object containing the values after the step.
   * @type {Object.<string, *>}
   */
  postStepValues;

  /**
   * Constructs a change object.
   * @param  {(Node|Edge)} object - A Node or Edge object.
   * @param  {Array.<string>} fields - Array of field names to be stored.
   * @param {Object.<string, *>} preValues - Object containing field names and their respective values before the step.
   * @param {Object.<string, *>} duringValues - Object containing field names and their respective values before the step.
   * @param {Object.<string, *>} postValues - Object containing field names and their respective values after the step.
   * @constructs Change
   */
  constructor(object, fields, preValues, duringValues, postValues) {
    this.object = object;
    this.preStepValues = {};
    this.duringStepValues = {};
    this.postStepValues = {};

    for (let field of fields) {
      if (field in preValues) {
        this.preStepValues[field] = preValues[field];
      }
      if (field in duringValues) {
        this.duringStepValues[field] = duringValues[field];
      }
      if (field in postValues) {
        this.postStepValues[field] = postValues[field];
      }
    }
  }

  /**
   * Set object values to those it had before the step.
   */
  applyPre() {
    for (let field of Object.keys(this.preStepValues)) {
      this.object[field] = this.preStepValues[field];
    }
  }

  /**
   * Set object values to those it had during the step.
   */
  applyDuring() {
    for (let field of Object.keys(this.duringStepValues)) {
      this.object[field] = this.duringStepValues[field];
    }
  }

  /**
   * Set object values to those it had after the step.
   */
  applyPost() {
    for (let field of Object.keys(this.postStepValues)) {
      this.object[field] = this.postStepValues[field];
    }
  }
}

export { Change };
export default Change;

import { Node } from '../data/node/node';
import { Edge } from '../data/edge/edge';
import Step from '../algorithm/step';
import Change from '../algorithm/change';

class StepBuilder {

  /**
   * The step being created;
   * @type {Step}
   */
  step = null;

  /**
   * Array of field names to store for Node objects.
   * @type {Array.<string>}
   */
  nodeFields;

  /**
   * Array of field names to store for Edge objects.
   * @type {Array.<string>}
   */
  edgeFields;

  constructor(nodeFields, edgeFields, timeline) {
    this.nodeFields = nodeFields;
    this.edgeFields = edgeFields;
    this.timeline = timeline;
  }

  newStep(description) {
    this.step = new Step(description);
  }

  /**
   * Add a Node or Edge and its values to the step.
   * @param {(Node|Edge)} object - Node or Edge object.
   * @param {Object.<string, *>} preValues - Object containing field names and their respective values before the step.
   * @param {Object.<string, *>} duringValues - Object containing field names and their respective values before the step.
   * @param {Object.<string, *>} postValues - Object containing field names and their respective values after the step.
   */
  addChange(object, preValues, duringValues, postValues) {
    if (this.step === null) {
      throw Error('Step not created yet.');
    }
    let fields;
    if (object instanceof Node) {
      fields = this.nodeFields;
    } else if (object instanceof Edge) {
      fields = this.edgeFields;
    }
    let change = new Change(object, fields, preValues, duringValues, postValues);
    this.step.addChange(change);
  }

  completeStep() {
    let step = this.step;
    this.step = null;
    this.timeline.addStep(step);
  }
}

export { StepBuilder };
export default StepBuilder;

/**
 * Represents a step of an algorithm.
 * @class Step
 */
class Step {
  /**
   * String describing the Step.
   * @type {string}
   */
  description;

  /**
   * Set of changes in this step.
   * @type {Set}
   */
  changes = new Set();

  /**
   * Constructs a Step.  Ensures description is not null or the empty string
   * @param  {string} description - A string describing the step of the algorithm.
   * @constructs Step
   */
  constructor(description) {
    this.description = description;
    if (!this.description) {
      this.description = ' ';
    }
  }

  /**
   * Add a change in values for an object to this Step.
   * @param {Change} change - Change object to add.
   */
  addChange(change) {
    this.changes.add(change);
  }

  /**
   * Apply all pre-step values to objects.
   */
  applyPre() {
    for (let change of this.changes) {
      change.applyPre();
    }
  }

  /**
   * Applies all step values to objects.
   */
  applyDuring() {
    for (let change of this.changes) {
      change.applyDuring();
    }
  }

  /**
   * Applies all post-step values to objects.
   */
  applyPost() {
    for (let change of this.changes) {
      change.applyPost();
    }
  }
}

export { Step };
export default Step;

export default class Stepper {

  result;
  speed = 500;
  interval;

  constructor() {
  }

  getAlgorithm() {
    return this.algorithm;
  }

  reset() {
    this.pause();
    this.result = null;
  }

  resetGraph() {
    while(this.result.stepIndex !== -1) {
      this.result.stepBackward();
    }
  }

  setResult(result) {
    this.reset();
    this.result = result;
  }

  updateStepGUI(description, stepNum, stepTotal) {
    let des = document.getElementsByClassName('algorithm-step-des');
    let num = document.getElementsByClassName('algorithm-step-num');

    if (des.length === 1 && num.length === 1) {
      des[0].innerHTML = 'Description:  ' + description;
      num[0].innerHTML = 'Step ' + stepNum + ' of ' + stepTotal;
    }
  }

  stepBackward() {
    this.updateStepGUI(this.result.stepBackward(), this.result.stepIndex, this.result.timeline.length);
  }


  stepForward() {
    this.updateStepGUI(this.result.stepForward(), this.result.stepIndex, this.result.timeline.length);
  }

  pause() {
    clearInterval(this.interval);
  }

  play() {
    this.interval = setInterval(() => {
      if (this.result.stepIndex < this.result.timeline.length) {
        try {
          this.stepForward();
        } catch (e) {
          this.pause();
          throw e;
        }
      } else {
        this.pause();
      }
    }, this.speed);
  }

}

import Queue from '../util/queue';
// import Stack from '../util/stack';
import { Node } from '../data/node/node';
import { Edge } from '../data/edge/edge';
import AlgorithmResult from '../algorithm/algorithm-result';
import AbstractAlgorithm from '../algorithm/abstract-algorithm';
import StepBuilder from '../algorithm/step-builder';

class TraversalAlgorithm extends AbstractAlgorithm {

  name = 'Traversal';
  // data structure that determines order of traversal
  next = new Queue();

  // starting point for the algorithm
  source = null;

  // things that the GUI needs to supply for the algorithm to run
  inputs = {
    source: {
      type: 'node',
      name: 'Source',
      test: (node) => {
        return node instanceof Node;
      },
      required: true
    }
  };

  result;

  // the members of the nodes and edges that the algorithm will edit
  nodeFields = [
    'color'
  ];
  edgeFields = [
    'color'
  ];

  constructor(graph) {
    super(graph);
    this.result = new AlgorithmResult();
    this.stepBuilder = new StepBuilder(this.nodeFields, this.edgeFields, this.result);
  }

  // visit the specified node
  visitNode(node) {
    // mark node as visited
    this.graphState.set(node, true);

    // add adjacent edges to the queue
    let edges = node.edges;
    for (let edge of edges) {
      if (this.hasVisited(edge) || this.next.has(edge)) {
        continue;
      }
      if (edge.isDirected && edge.startNode === node) {
        this.next.enqueue(edge);
      } else {
        this.next.enqueue(edge);
      }
    }
  }

  // visit the specified edge
  visitEdge(edge) {
    // mark edge as visited
    this.graphState.set(edge, true);

    // add adjacent node to the queue
    if (!this.hasVisited(edge.destNode) && !this.next.has(edge.destNode)) {
      this.next.enqueue(edge.destNode);
    } else if (!this.hasVisited(edge.startNode) && !edge.isDirected && !this.next.has(edge.startNode)) {
      this.next.enqueue(edge.startNode);
    }
  }

  // check if object has been visited by algorithm
  hasVisited(object) {
    return this.graphState.has(object) && this.graphState.get(object);
  }

  // run the next step of the algorithm
  step() {
    if (this.hasStarted && this.next.size === 0) {
      this.isComplete = true;
      return false;
    }

    let nextItem;

    if (this.hasStarted) {
      nextItem = this.next.dequeue();
    } else {
      this.hasStarted = true;
      nextItem = this.source;
    }

    if (nextItem instanceof Node) {
      this.visitNode(nextItem);
    } else if (nextItem instanceof Edge) {
      this.visitEdge(nextItem);
    } else {
      throw Error('Non-graph object in algorithm queue');
    }
    // represent the visual aspects of this step by creating a new step, adding a change for the current object, and completing the step
    this.stepBuilder.newStep(`Visiting ${nextItem.constructor.name} ${nextItem.id}`);
    this.stepBuilder.addChange(nextItem, {
      color: 'black'
    }, {
      color: 'red'
    }, {
      color: 'green'
    });
    this.stepBuilder.completeStep();

    return true;
  }

}

export { TraversalAlgorithm };
export default TraversalAlgorithm;

require('../scss/styles.scss');

import { Graph } from './data/graph';
import ui from './ui/ui';

let graph;

function init() {
  graph = new Graph();
  ui.init(graph);
  ui.toolbar.selectToolByName('node');
  ui.canvas.resize();
  window.requestAnimationFrame(draw);
}

function draw() {
  ui.canvas.clear();
  graph.draw(ui.canvas.context);
  window.requestAnimationFrame(draw);
}

window.addEventListener('load', init, false);

window.addEventListener('resize', (event) => {
  if (ui.canvas) {
    ui.canvas.resize();
  }
}, false);

import Edge from './edge';

class DashedEdge extends Edge {

  draw(context) {
    context.strokeStyle = this.isSelected ? this.selectedColor : this.color;
    context.lineWidth = this.lineWidth;
    context.setLineDash([ 5, 5 ]);

    // Create a new path
    context.beginPath();

    // Start path at given point
    context.moveTo(this.startPoint.x, this.startPoint.y);

    // Draw line to given point
    context.quadraticCurveTo(this.bezierPoint.x, this.bezierPoint.y, this.destPoint.x, this.destPoint.y);

    // Draw to the canvas
    context.stroke();
    context.setLineDash([]);

    if (this.isDirected) {
      context.fillStyle = this.isSelected ? this.selectedColor : this.color;
      this.drawArrow(context);
    }

    if (this.edgeLabel !== '') {
      this.drawLabel(context);
    }
  }

}

export { DashedEdge };
export default DashedEdge;

import { calcBezierDistance, bezierDerivative } from '../../util/bezier';
import Point2D from '../../util/point-2d';
import Triangle2D from '../../util/triangle-2d';
import Line2D from '../../util/line-2d';
// import Vector2D from '../../util/vector-2d';
import Label from '../label';

const EDGE_DISTANCE_THRESHOLD = 10;

class Edge {

  static numEdges = 0;
  id = Edge.numEdges++;

  // graph data
  startNode;
  destNode;
  isDirected;
  startPoint = null;
  destPoint = null;
  bezierPoint = null;
  partners = [];

  // status
  isSelected = false;

  // label
  label;

  // appearance
  color = '#000000';
  selectedColor = '#FF0000';
  lineWidth = 1;

  constructor(startNode, destNode) {
    let methods = [
      'draw'
    ];

    for (let method of methods) {
      if (typeof this[method] !== 'function') {
        throw TypeError('Must override method: ' + method);
      }
    }

    if (typeof startNode === 'undefined' || typeof destNode === 'undefined') {
      throw Error(`Edge constructor requires at least two arguments: startNode and destNode. Actually passed in ${startNode}, ${destNode}`);
    }

    this.startNode = startNode;
    this.destNode = destNode;

    startNode.edges.add(this);
    destNode.edges.add(this);

    // copy partners from an existing partner edge
    for (let edge of this.startNode.edges) {
      if (edge.startNode === this.startNode && edge.destNode === this.destNode) {
        this.partners = edge.partners.slice(0);
        break;
      } else if (edge.destNode === this.startNode && edge.startNode === this.destNode) {
        this.partners = edge.partners.slice(0);
        break;
      }
    }

    // add this edge to partners field of all partner edges
    for (let i = 0; i < this.partners.length; i++) {
      this.partners[i].partners.push(this);
    }
    this.partners.push(this);

    if (this.startNode === this.destNode) {
      this.isDirected = true;
      this.updateEndpoints();
    } else {
      for (let i = 0; i < this.partners.length; i++) {
        this.partners[i].updateEndpoints();
      }
    }

    this.label = new Label(this.bezierPoint.x, this.bezierPoint.y, this);
  }

  detach() {
    // remove this edge from partners of all partner edges
    for (let i = 0; i < this.partners.length; i++) {
      if (this.partners[i] === this) {
        continue;
      }
      let index = this.partners[i].partners.indexOf(this);
      this.partners[i].partners.splice(index, 1);
      this.partners[i].updateEndpoints();
    }

    this.startNode.edges.delete(this);
    this.destNode.edges.delete(this);
    this.startNode = null;
    this.destNode = null;
  }

  updateSelfLoopEndpoints() {
    this.startPoint = this.startNode.getAnglePoint(240);
    this.destPoint = this.startNode.getAnglePoint(300);
    this.bezierPoint = {
      x: (this.startPoint.x + this.destPoint.x) / 2,
      y: this.startPoint.y - 2 * this.startNode.radius
    };
  }

  updateNormalEdgeEndpoints() {
    let dx = this.destNode.x - this.startNode.x;
    let dy = this.destNode.y - this.startNode.y;
    let distance = Math.sqrt(dx * dx + dy * dy);

    // calculate incline based on dy and distance
    // this is the angle between x-axis and the line from startNode to destNode
    let incline = Math.asin(dy / distance);
    // convert from radians to degrees
    incline = incline * 180 / Math.PI;

    // Note that canvas coordinates increase towards the bottom right
    // Quadrants are oriented as follows:
    //       |
    //   Q3  |  Q4
    //       |
    // ------------- +x
    //       |
    //   Q2  |  Q1
    //       |
    //       +y
    //

    // if destNode.x < startNode.x, the angle should end:
    //   in the second quadrant if destNode.y > startNode.y
    //   in the third quadrant if destNode.y < startNode.y
    if (this.startNode.x >= this.destNode.x) {
      if (this.startNode.y >= this.destNode.y) {
        incline = (540 - incline) % 360;
      } else {
        incline = 180 - incline;
      }
    }

    let numPartners = this.partners.length + 1;
    let multiIndex = this.partners.indexOf(this) + 1;

    let ratio = multiIndex / numPartners;

    // if the partner edge with index 0 is going in the opposite direction compared to the current edge
    // then draw the current edge on the opposite side of the line connecting the two nodes
    if (this.partners[0].startNode !== this.startNode) {
      ratio = 1 - ratio;
    }

    let startAngle = incline + 90 * ratio - 45;
    let destAngle = incline + 225 - 90 * ratio;
    this.startPoint = this.startNode.getAnglePoint(startAngle);
    this.destPoint = this.destNode.getAnglePoint(destAngle);

    let orient = ratio - 0.5;
    let diff = {
      x: this.destPoint.x - this.startPoint.x,
      y: this.destPoint.y - this.startPoint.y
    };
    this.bezierPoint = {
      x: (this.startPoint.x + this.destPoint.x) / 2 - orient * diff.y,
      y: (this.startPoint.y + this.destPoint.y) / 2 + orient * diff.x
    };
  }

  updateEndpoints() {
    let oldStartPoint = this.startPoint;
    let oldBezierPoint = this.bezierPoint;
    let oldDestPoint = this.destPoint;
    if (this.startNode === this.destNode) {
      this.updateSelfLoopEndpoints();
    } else {
      this.updateNormalEdgeEndpoints();
    }
    if (this.label) {
      if (oldStartPoint === null || oldBezierPoint === null || oldDestPoint === null) {
        // initial label location
        this.label.x = this.bezierPoint.x;
        this.label.y = this.bezierPoint.y;
      } else if (this.startNode === this.destNode) {
        // self loop case
        this.updateSelfLoopLabel(oldBezierPoint);
      } else {
        let oldStartPoint2D = new Point2D(oldStartPoint.x, oldStartPoint.y);
        let oldBezierPoint2D = new Point2D(oldBezierPoint.x, oldBezierPoint.y);
        let oldDestPoint2D = new Point2D(oldDestPoint.x, oldDestPoint.y);

        let startPoint2D = new Point2D(this.startPoint.x, this.startPoint.y);
        let bezierPoint2D = new Point2D(this.bezierPoint.x, this.bezierPoint.y);
        let destPoint2D = new Point2D(this.destPoint.x, this.destPoint.y);

        let oldStartDest = new Line2D(oldStartPoint2D, oldDestPoint2D);
        let startDest = new Line2D(startPoint2D, destPoint2D);
        if (startDest.hasPoint(bezierPoint2D)) {
          if (startPoint2D.equals(destPoint2D)) {
            // corner case where startPoint == endPoint
            this.label.x = startPoint2D.x;
            this.label.y = startPoint2D.y;
          }

          // straight edge case
          if (!oldStartDest.hasPoint(oldBezierPoint2D)) {
            // was previously a bezier curve, so reset coordinates to bezierPoint
            this.label.x = this.bezierPoint.x;
            this.label.y = this.bezierPoint.y;
            return;
          }
          this.updateStraightEdgeLabel(oldStartPoint2D, oldBezierPoint2D, oldDestPoint2D, startPoint2D, bezierPoint2D, destPoint2D);
        } else {
          if (oldStartDest.hasPoint(oldBezierPoint2D)) {
            // was previously a straight line, so reset coordinates to bezierPoint
            this.label.x = this.bezierPoint.x;
            this.label.y = this.bezierPoint.y;
            return;
          }
          // curved edge
          this.updateCurvedEdgeLabel(oldStartPoint2D, oldBezierPoint2D, oldDestPoint2D, startPoint2D, bezierPoint2D, destPoint2D);
        }
      }
    }
  }

  containsPoint(x, y) {
    return EDGE_DISTANCE_THRESHOLD > calcBezierDistance(x, y, this.startPoint, this.bezierPoint, this.destPoint);
  }

  draw(context) {
    throw Error('Can\'t call draw from abstract Edge class.');
  }

  drawLabel(context) {
    this.label.draw(context);
  }

  drawArrow(context) {
    let slope = bezierDerivative(1, this.startPoint, this.bezierPoint, this.destPoint);
    let length = Math.sqrt(slope.x * slope.x + slope.y * slope.y);
    // normalize slope
    slope = { x: slope.x / length, y: slope.y / length };
    // perpendicular:
    context.beginPath();
    context.moveTo(this.destPoint.x, this.destPoint.y);
    context.lineTo(this.destPoint.x - 15 * slope.x - 5 * slope.y, this.destPoint.y - 15 * slope.y + 5 * slope.x);
    context.lineTo(this.destPoint.x - 9 * slope.x, this.destPoint.y - 9 * slope.y);
    context.lineTo(this.destPoint.x - 15 * slope.x + 5 * slope.y, this.destPoint.y - 15 * slope.y - 5 * slope.x);
    context.fill();
  }

  updateSelfLoopLabel(oldBezierPoint) {
    this.label.x += this.bezierPoint.x - oldBezierPoint.x;
    this.label.y += this.bezierPoint.y - oldBezierPoint.y;
  }

  updateStraightEdgeLabel(oldStartPoint2D, oldBezierPoint2D, oldDestPoint2D, startPoint2D, bezierPoint2D, destPoint2D) {
    let oldLabelPosition = new Point2D(this.label.x, this.label.y);
    let oldStartLabelVec = oldStartPoint2D.vectorTo(oldLabelPosition);
    let oldStartDestVec = oldStartPoint2D.vectorTo(oldDestPoint2D);

    let u = oldStartLabelVec.projectOnto(oldStartDestVec);
    let v = oldStartLabelVec.sub(u);

    let ratioU = u.length / oldStartDestVec.length;
    if (u.degreesTo(oldStartDestVec) !== 0) {
      ratioU *= -1;
    }

    let startDestVec = startPoint2D.vectorTo(destPoint2D);
    let newU = startDestVec.scale(ratioU);

    let newLabelPosition;
    if (v.length === 0) {
      // label is on the line
      newLabelPosition = startPoint2D.translateVec(newU);
    } else {
      // label is not on the line
      let ratioV = v.length / oldStartDestVec.length;
      let angle = oldStartDestVec.degreesTo(v);
      let newV = startDestVec.rotateDegrees(angle).scale(ratioV);

      newLabelPosition = startPoint2D.translateVec(newU).translateVec(newV);
    }

    this.label.x = newLabelPosition.x;
    this.label.y = newLabelPosition.y;
  }

  updateCurvedEdgeLabel(oldStartPoint2D, oldBezierPoint2D, oldDestPoint2D, startPoint2D, bezierPoint2D, destPoint2D) {
    let oldTriangle = new Triangle2D(oldStartPoint2D, oldBezierPoint2D, oldDestPoint2D);
    let newTriangle = new Triangle2D(startPoint2D, bezierPoint2D, destPoint2D);

    let oldLabelPosition = new Point2D(this.label.x, this.label.y);
    let newLabelPosition = oldLabelPosition.relativePositionToTriangle2D(oldTriangle, newTriangle);

    this.label.x = newLabelPosition.x;
    this.label.y = newLabelPosition.y;
  }

}

