import MouseHandler from '../util/mouse-handler';
import ContextMenu from '../ui/context-menu';

const SCALE_MODIFIER = 0.9;

class Canvas {

  ui;

  canvas;
  context;

  // scale of 0.5 means drawing at half size (0.5 pixels for each original pixel)
  scaleValue = 1;
  dx = 0;
  dy = 0;

  mouseHandler;
  contextMenu;

  constructor(ui) {
    this.ui = ui;
    this.canvas = document.getElementById('canvas');
    this.context = this.canvas.getContext('2d');
  }

  init(graph) {
    this.mouseHandler = new MouseHandler(graph);
    this.contextMenu = new ContextMenu(this.ui, this.mouseHandler);
    this.initListeners();
  }

  resize(event) {
    this.context.canvas.width = window.innerWidth;
    this.context.canvas.height = window.innerHeight;
    this.update();
  }

  get scale() {
    return this.scaleValue;
  }

  set scale(scale) {
    this.scaleValue = scale;
    this.ui.statusBar.updateZoom(scale);
  }

  reset() {
    this.dx = 0;
    this.dy = 0;
    this.scale = 1;
    this.update();
    this.ui.statusBar.updateMouse(0, 0);
  }

  clear() {
    this.context.clearRect(this.dx, this.dy, this.canvas.width / this.scale, this.canvas.height / this.scale);
  }

  getCanvasX(event) {
    let canvasX;
    if (event.target === this.canvas) {
      canvasX = event.offsetX;
    } else {
      let offsets = document.getElementById('canvas').getBoundingClientRect();
      canvasX = event.screenX - offsets.left;
    }
    let x = canvasX / this.scale + this.dx;
    return x;
  }

  getCanvasY(event) {
    let canvasY;
    if (event.target === this.canvas) {
      canvasY = event.offsetY;
    } else {
      let offsets = document.getElementById('canvas').getBoundingClientRect();
      canvasY = event.screenY - offsets.top;
    }
    let y = canvasY / this.scale + this.dy;
    return y;
  }

  update() {
    // reset the transformations done to the canvas
    this.context.resetTransform();

    // context.setTransform(xScale, xSkew, ySkew, yScale, dx, dy) applies
    // the translation before the scale.
    // This is not what we want, since the scale is done relative to (0,0)
    // and the position displayed at the top-left corner will no longer
    // be (dx,dy), which means the coordinates under the mouse will change.

    // scale both x- and y-axis
    this.context.scale(this.scale, this.scale);

    // context.translate(a, b) translates the canvas origin by (a,b).
    // Since (dx,dy) are the coordinates calculated for the new origin, the
    // canvas needs to be translated by (-dx,-dy).
    this.context.translate(-1 * this.dx, -1 * this.dy);
  }

  initListeners() {
    this.canvas.addEventListener('mousedown', (event) => {
      event.preventDefault();
      event.stopPropagation();
      let x = this.getCanvasX(event);
      let y = this.getCanvasY(event);

      if (event.button === 0 && !this.contextMenu.isDisplayed) {
        this.mouseHandler.downListener(event, this.ui.toolbar.currentTool, x, y);
      } else if (event.button === 2) {
        this.mouseHandler.rightDownListener(event, x, y);
      }

      if (this.contextMenu.isDisplayed) {
        this.contextMenu.toggleContextMenu();
      }
    });

    window.addEventListener('mouseup', (event) => {
      event.preventDefault();
      event.stopPropagation();
      let x = this.getCanvasX(event);
      let y = this.getCanvasY(event);

      if (event.button === 0) {
        this.mouseHandler.upListener(event, this.ui.toolbar.currentTool, x, y);
      } else if (event.button === 2) {
        this.mouseHandler.rightUpListener(event, x, y);
      }
    });

    window.addEventListener('mousemove', (event) => {
      event.preventDefault();
      event.stopPropagation();
      let x = this.getCanvasX(event);
      let y = this.getCanvasY(event);

      if (event.button === 0 && event.buttons !== 2) {
        this.mouseHandler.moveListener(event, this.ui.toolbar.currentTool, x, y);
      } else if (event.button === 2 || event.buttons === 2) {
        this.mouseHandler.rightMoveListener(event, x, y);
      }
    });

    window.addEventListener('contextmenu', (event) => {
      if (event.target === this.canvas) {
        // prevent default context menu
        event.preventDefault();
      }
      event.stopPropagation();
      let x = this.getCanvasX(event);
      let y = this.getCanvasY(event);

      this.mouseHandler.contextmenuEventListener(event, x, y, this.contextMenu);
    });

    this.canvas.addEventListener('wheel', (event) => {
      event.stopPropagation();
      // prevent page scrolling (the default scroll behavior)
      event.preventDefault();

      // store the current scale value
      let oldScale = this.scale;

      // get the amount the mousewheel was scrolled
      let delta = event.deltaY;
      if (delta > 0) {
        // scroll down
        this.scale = Math.max(this.scale * SCALE_MODIFIER, 0.1);
      } else if (delta < 0) {
        // scroll up
        this.scale = Math.min(this.scale / SCALE_MODIFIER, 10);
      }

      // get the mouse position (relative to the canvas element)
      let mouseX = event.offsetX;
      let mouseY = event.offsetY;

      // Calculate the new position of the displayed origin (top left corner).
      // (dx,dy) are the coordinates of the top-left corner.
      // (x1,y1) and (x2,y2) are the coordinates at the mouse position before
      // and after changing the scale, respectively.
      //
      // With the old scale value:
      //     dx1 = dx
      //     dy1 = dy
      //     x1 = dx + mouseX / oldScale
      //     y1 = dy + mouseY / oldScale
      //
      // With new scale:
      //     dx2 = dx + mouseX / oldScale - mouseX / scale
      //     dy2 = dy + mouseY / oldScale - mouseY / scale
      //     x2 = dx2 + mouseX / scale
      //        = dx + mouseX / oldScale - mouseX / scale + mouseX / scale
      //        = dx + mouseX / oldScale
      //        = x1
      //     y2 = dy2 + mouseY / scale
      //        = dy + mouseY / oldScale - mouseY / scale + mouseY / scale
      //        = dy + mouseY / oldScale
      //        = y1
      //
      // Note that (x1,y1) = (x2,y2) which means the coordinates under
      // the mouse stays the same.
      this.dx += mouseX / oldScale - mouseX / this.scale;
      this.dy += mouseY / oldScale - mouseY / this.scale;

      this.update();

      this.ui.statusBar.updateZoom(this.scale);
    });

    this.canvas.addEventListener('selectstart', (event) => {
      event.stopPropagation();
      event.preventDefault();
      return false;
    });

    this.canvas.addEventListener('dblclick', (event) => {
      event.stopPropagation();
      event.preventDefault();
      return false;
    });
  }
}

export { Canvas };
export default Canvas;

import Node from '../data/node/node';
import Edge from '../data/edge/edge';

const MENU_TOGGLE_CLASS = 'context-menu--active';

const MENU_SECTION_CLASS = 'context-menu__section';
const MENU_SECTION_VISIBILITY_CLASS = 'context-menu__section--visible';
const MENU_SECTION_NAME_ATTRIBUTE = 'data-name';

const MENU_ITEM_TYPE_ATTRIBUTE = 'data-type';
const MENU_ITEM_DATA_ATTRIBUTE = 'data-data';

class ContextMenu {

  ui;

  mouseHandler;

  contextMenu;

  isDisplayed = false;
  menuPosX;
  menuPosY;

  currentSection = null;
  component;

  constructor(ui, mouseHandler) {
    this.ui = ui;
    this.mouseHandler = mouseHandler;
    this.contextMenu = document.getElementById('context-menu');
    this.initListeners();
  }

  initListeners() {
    this.contextMenu.addEventListener('mouseup', (event) => {
      let type = event.target.getAttribute(MENU_ITEM_TYPE_ATTRIBUTE);
      let value = event.target.getAttribute(MENU_ITEM_DATA_ATTRIBUTE);

      if (type === 'add') {
        this.mouseHandler.contextAdd(value, this.menuPosX, this.menuPosY);
      } else if (type === 'toggle') {
        this.mouseHandler.contextToggle(value);
      } else if (type === 'delete') {
        this.mouseHandler.contextDelete();
      } else if (type === 'edit') {
        this.mouseHandler.contextSelect(event, this.ui.toolbar.currentTool, this.menuPosX, this.menuPosY);
      } else if (type === 'save') {
        let downloadLink = document.createElement('a');
        downloadLink.href = this.ui.canvas.canvas.toDataURL('image/png');
        downloadLink.download = 'canvas.png';
        downloadLink.target = '_blank';
        document.body.appendChild(downloadLink);
        downloadLink.dispatchEvent(new MouseEvent('click'));
        document.body.removeChild(downloadLink);
      }

      if (event.button !== 2) {
        this.toggleContextMenu();
      }
    });

    this.contextMenu.addEventListener('contextmenu', (event) => {
      event.preventDefault();
      this.toggleContextMenu();
    });

    document.addEventListener('click', (event) => {
      if (!this.contextMenu.contains(event.target)) {
        if (this.isDisplayed) {
          this.toggleContextMenu();
        }
      }
    });
  }

  toggleContextMenu() {
    if (this.isDisplayed) {
      this.contextMenu.classList.remove(MENU_TOGGLE_CLASS);
    } else {
      this.contextMenu.classList.add(MENU_TOGGLE_CLASS);
    }
    this.isDisplayed = !this.isDisplayed;
  }

  repositionMenu(event) {
    let xpos = event.clientX;
    let ypos = event.clientY;

    this.contextMenu.style.left = xpos + 'px';
    this.contextMenu.style.top = ypos + 'px';

    let distanceRight = document.body.offsetWidth - xpos - this.contextMenu.offsetWidth;
    let distanceBottom = document.body.offsetHeight - ypos - this.contextMenu.offsetHeight;

    if (distanceRight < 0) {
      this.contextMenu.style.left = `${xpos + distanceRight}px`;
    }
    if (distanceBottom < 0) {
      this.contextMenu.style.top = `${ypos - this.contextMenu.offsetHeight}px`;
    }
  }

  contextmenuEventListener(event, x, y) {
    this.menuPosX = x;
    this.menuPosY = y;

    this.component = this.mouseHandler.contextComponent(event, this.menuPosX, this.menuPosY);

    this.toggleContextMenu();

    // hide all menu sections
    let sectionElements = this.contextMenu.querySelectorAll(`.${MENU_SECTION_CLASS}`);
    for (let i = 0; i < sectionElements.length; i++) {
      sectionElements[i].classList.remove(MENU_SECTION_VISIBILITY_CLASS);
    }

    // populate array with names of menu sections to be displayed
    let sections = [];
    if (this.component !== null) {
      sections.push('component');
      if (this.component instanceof Node) {
        sections.push('node');
      } else if (this.component instanceof Edge) {
        sections.push('edge');
      }
    } else {
      sections.push('default');
    }
    sections.push('action');

    // make menu sections visible
    for (let section of sections) {
      let sectionElement = this.contextMenu.querySelector(`.${MENU_SECTION_CLASS}[${MENU_SECTION_NAME_ATTRIBUTE}="${section}"]`);
      sectionElement.classList.add(MENU_SECTION_VISIBILITY_CLASS);
    }

    this.repositionMenu(event);
  }

}

export { ContextMenu };
export default ContextMenu;

const DROPDOWN_CLASS = 'dropdown';
const DROPDOWN_HIDDEN_CLASS = 'dropdown--hidden';
const DROPDOWN_TOGGLE_CLASS = 'dropdown--active';

const DROPDOWN_SELECT_CLASS = 'dropdown__select';

