/**
 * Class for representing 2D vectors.
 * @class Vector2D
 */
class Vector2D {

  /**
   * x component of the vector.
   * @type {number}
   */
  x;

  /**
   * y component of the vector.
   * @type {number}
   */
  y;

  /**
   * Constructs a Vector2D object.
   * @param  {number} x - x-component of the vector.
   * @param  {number} y - y-component of the vector.
   * @constructs Vector2D
   */
  constructor(x, y) {
    this.x = x;
    this.y = y;
  }

  /**
