      let node = new CircleNode(0, 0);
      let radius = node.radius;
      graph.addNode(node);

      nodeTool.selectNone(null, graph, radius + 1, 0);
      (graph.nodes.size).should.be.equal(1);
    });
  });

  // should do same thing as selectNone
  describe('#dropOnNone', () => {
