// import Queue from '../util/queue';
// import Stack from '../util/stack';
// import { Node } from '../data/node/node';
// import { Edge } from '../data/edge/edge';

/**
 * Abstract class for algorithms.
 * @class AbstractAlgorithm
 */
class AbstractAlgorithm {

  /**
   * Name of the algorithm.
   * @type {string}
   */
  name;
  /**
   * Reference to the graph the algorithm is running on.
   * @type {Graph}
   */
  graph;

  /**
   * Data structure that determines order of traversal.
   * @type {Stack|Queue}
   */
  next;

  /**
   * Map that stores algorithm values.
   * @type {WeakMap}
   */
  graphState = new WeakMap();

  /**
   * Whether the algorithm is complete or not.
   * @type {boolean}
   */
  isComplete = false;

  /**
   * Whether the algorithm has started or not.
   * @type {boolean}
   */
  hasStarted = false;

  /**
   * @typedef InputType
   * @type {object}
   * @property {string} type - String representing tyoe of input.
   * @property {string} name - Name to be displayed.
   * @property {method} test - Function run to validate input.
   * @property {boolean} required - Whether or not the input is required.
   */

  /**
   * Object that defines the algorithm input types.
   * @type {Object.<string, InputType>}
   */
  inputs = {};

  result;

  nodeFields = [];
  edgeFields = [];

  stepBuilder;

  constructor(graph) {
    this.graph = graph;
  }

  getResult() {
    return this.result;
  }

  // run the next step of the algorithm
  step() {}

}

export { AbstractAlgorithm };
export default AbstractAlgorithm;

// import { Node } from '../data/node/node';
// import { Edge } from '../data/edge/edge';
// import Step from '../algorithm/step';

/**
 * Stores the results generated by running an Algorithm. An instance will be stored in each Algorithm object.
 * @class AlgorithmResult
 */
class AlgorithmResult {

  /**
   * Array of Step objects.
   * @type {Array.<Step>}
   */
  timeline;

  /**
   * Current index in the timeline.
   * @type {number}
   */
  stepIndex;

  /**
   * Constructs an instance of AlgorithmResult.
   * @param  {Array.<string>} nodeFields - Array of fields to store changes for in Node objects.
   * @param  {Array.<string>} edgeFields - Array of fields to store changes for in Edge objects.
   * @constructs AlgorithmResult
   */
  constructor() {
    this.timeline = [];
    this.stepIndex = -1;
  }

  stepForward() {
    if (this.stepIndex === this.timeline.length) {
      return 'Algorithm finished!';
    }
    if (this.stepIndex > -1) {
      let finishingStep = this.timeline[this.stepIndex];
      finishingStep.applyPost();
    }
    if (this.stepIndex < this.timeline.length - 1) {
      let nextStep = this.timeline[this.stepIndex + 1];
      nextStep.applyDuring();
      this.stepIndex++;
      return nextStep.description;
    }
    this.stepIndex++;
    return 'Done!';
  }

  stepBackward() {
    if (this.stepIndex === -1) {
      return 'Algorithm ready to begin!';
    }
    if (this.stepIndex < this.timeline.length) {
      let redactingStep = this.timeline[this.stepIndex];
      redactingStep.applyPre();
    }
    if (this.stepIndex > 0) {
      let previousStep = this.timeline[this.stepIndex - 1];
      previousStep.applyDuring();
      this.stepIndex--;
      return previousStep.description;
    }
    this.stepIndex--;
    return 'Ready!';
  }

  /**
   * Add a Step to the AlgorithmResult.
   * @param {Step} step - The step to add.
   */
  addStep(step) {
    this.timeline.push(step);
  }
}

export { AlgorithmResult };
export default AlgorithmResult;

/**
 * Represents changes for a Node or Edge during a step of the algorithm.
 * @class Change
 */
class Change {

  /**
   * Object that the Change is storing values for.
   * @type {(Node|Edge)}
   */
  object;

  /**
   * Object containing the values before the step.
   * @type {Object.<string, *>}
   */
  preStepValues;

  /**
   * Object containing the values during the step.
   * @type {Object.<string, *>}
   */
  duringStepValues;

  /**
   * Object containing the values after the step.
   * @type {Object.<string, *>}
   */
  postStepValues;

  /**
   * Constructs a change object.
   * @param  {(Node|Edge)} object - A Node or Edge object.
   * @param  {Array.<string>} fields - Array of field names to be stored.
   * @param {Object.<string, *>} preValues - Object containing field names and their respective values before the step.
   * @param {Object.<string, *>} duringValues - Object containing field names and their respective values before the step.
   * @param {Object.<string, *>} postValues - Object containing field names and their respective values after the step.
   * @constructs Change
   */
  constructor(object, fields, preValues, duringValues, postValues) {
    this.object = object;
    this.preStepValues = {};
    this.duringStepValues = {};
    this.postStepValues = {};

    for (let field of fields) {
      if (field in preValues) {
        this.preStepValues[field] = preValues[field];
      }
      if (field in duringValues) {
        this.duringStepValues[field] = duringValues[field];
      }
      if (field in postValues) {
        this.postStepValues[field] = postValues[field];
      }
    }
  }

  /**
   * Set object values to those it had before the step.
   */
  applyPre() {
    for (let field of Object.keys(this.preStepValues)) {
      this.object[field] = this.preStepValues[field];
    }
  }

  /**
   * Set object values to those it had during the step.
   */
  applyDuring() {
    for (let field of Object.keys(this.duringStepValues)) {
      this.object[field] = this.duringStepValues[field];
    }
  }

  /**
   * Set object values to those it had after the step.
   */
  applyPost() {
    for (let field of Object.keys(this.postStepValues)) {
      this.object[field] = this.postStepValues[field];
    }
  }
}

export { Change };
export default Change;

import { Node } from '../data/node/node';
import { Edge } from '../data/edge/edge';
import Step from '../algorithm/step';
import Change from '../algorithm/change';

class StepBuilder {

  /**
   * The step being created;
   * @type {Step}
   */
  step = null;

  /**
   * Array of field names to store for Node objects.
   * @type {Array.<string>}
   */
  nodeFields;

  /**
   * Array of field names to store for Edge objects.
   * @type {Array.<string>}
   */
  edgeFields;

  constructor(nodeFields, edgeFields, timeline) {
    this.nodeFields = nodeFields;
    this.edgeFields = edgeFields;
    this.timeline = timeline;
  }

  newStep(description) {
    this.step = new Step(description);
  }

  /**
   * Add a Node or Edge and its values to the step.
   * @param {(Node|Edge)} object - Node or Edge object.
   * @param {Object.<string, *>} preValues - Object containing field names and their respective values before the step.
   * @param {Object.<string, *>} duringValues - Object containing field names and their respective values before the step.
   * @param {Object.<string, *>} postValues - Object containing field names and their respective values after the step.
   */
  addChange(object, preValues, duringValues, postValues) {
    if (this.step === null) {
      throw Error('Step not created yet.');
    }
    let fields;
    if (object instanceof Node) {
      fields = this.nodeFields;
    } else if (object instanceof Edge) {
      fields = this.edgeFields;
    }
    let change = new Change(object, fields, preValues, duringValues, postValues);
    this.step.addChange(change);
  }

  completeStep() {
    let step = this.step;
    this.step = null;
    this.timeline.addStep(step);
  }
}

export { StepBuilder };
export default StepBuilder;

/**
 * Represents a step of an algorithm.
 * @class Step
 */
class Step {
  /**
   * String describing the Step.
   * @type {string}
   */
  description;

  /**
   * Set of changes in this step.
   * @type {Set}
   */
  changes = new Set();

  /**
   * Constructs a Step.  Ensures description is not null or the empty string
   * @param  {string} description - A string describing the step of the algorithm.
   * @constructs Step
   */
  constructor(description) {
    this.description = description;
    if (!this.description) {
      this.description = ' ';
    }
  }

  /**
   * Add a change in values for an object to this Step.
   * @param {Change} change - Change object to add.
   */
  addChange(change) {
    this.changes.add(change);
  }

  /**
   * Apply all pre-step values to objects.
   */
  applyPre() {
    for (let change of this.changes) {
      change.applyPre();
    }
  }

  /**
   * Applies all step values to objects.
   */
  applyDuring() {
    for (let change of this.changes) {
      change.applyDuring();
    }
  }

  /**
   * Applies all post-step values to objects.
   */
  applyPost() {
    for (let change of this.changes) {
      change.applyPost();
    }
  }
}

export { Step };
export default Step;

// NOTES:  make this responsible for GUI elements as well, move to UI folder?  should draw knowing little to nothing about container, difficult to not make assumptions though
// import * as AlgorithmInterface from 'ui/algorithm';

export default class Stepper {

  result;
  speed = 500;
  interval;

  constructor() {
  }

  getAlgorithm() {
    return this.algorithm;
  }

  reset() {
    this.pause();
    this.result = null;
  }

  setResult(result) {
    this.reset();
    this.result = result;
  }

  updateStepGUI(description, stepNum, stepTotal) {
    let des = document.getElementsByClassName('algorithm-step-des');
    let num = document.getElementsByClassName('algorithm-step-num');

    if (des.length === 1 && num.length === 1) {
      des[0].innerHTML = 'Description:  ' + description;
      num[0].innerHTML = 'Step ' + stepNum + ' of ' + stepTotal;
    }
  }

  stepBackward() {
    this.updateStepGUI(this.result.stepBackward(), this.result.stepIndex, this.result.timeline.length);
  }


  stepForward() {
    this.updateStepGUI(this.result.stepForward(), this.result.stepIndex, this.result.timeline.length);
  }

  pause() {
    clearInterval(this.interval);
  }

  play() {
    this.interval = setInterval(() => {
      if (this.result.stepIndex < this.result.timeline.length) {
        try {
          this.stepForward();
        } catch (e) {
          this.pause();
          throw e;
        }
      } else {
        this.pause();
      }
    }, this.speed);
  }

}

import Queue from '../util/queue';
// import Stack from '../util/stack';
import { Node } from '../data/node/node';
import { Edge } from '../data/edge/edge';
import AlgorithmResult from '../algorithm/algorithm-result';
import AbstractAlgorithm from '../algorithm/abstract-algorithm';
import StepBuilder from '../algorithm/step-builder';

class TraversalAlgorithm extends AbstractAlgorithm {

  name = 'Traversal';
  // data structure that determines order of traversal
  next = new Queue();

  // starting point for the algorithm
  source = null;

  // things that the GUI needs to supply for the algorithm to run
  inputs = {
    source: {
      type: 'node',
      name: 'Source',
      test: (node) => {
        return node instanceof Node;
      },
      required: true
    }
  };

  result;

  // the members of the nodes and edges that the algorithm will edit
  nodeFields = [
    'color'
  ];
  edgeFields = [
    'color'
  ];

  constructor(graph) {
    super(graph);
    this.result = new AlgorithmResult();
    this.stepBuilder = new StepBuilder(this.nodeFields, this.edgeFields, this.result);
  }

  // visit the specified node
  visitNode(node) {
    // mark node as visited
    this.graphState.set(node, true);

    // add adjacent edges to the queue
    let edges = node.edges;
    for (let edge of edges) {
      if (this.hasVisited(edge) || this.next.has(edge)) {
        continue;
      }
      if (edge.isDirected && edge.startNode === node) {
        this.next.enqueue(edge);
      } else {
        this.next.enqueue(edge);
      }
    }
  }

  // visit the specified edge
  visitEdge(edge) {
    // mark edge as visited
    this.graphState.set(edge, true);

    // add adjacent node to the queue
    if (!this.hasVisited(edge.destNode) && !this.next.has(edge.destNode)) {
      this.next.enqueue(edge.destNode);
    } else if (!this.hasVisited(edge.startNode) && !edge.isDirected && !this.next.has(edge.startNode)) {
      this.next.enqueue(edge.startNode);
    }
  }

  // check if object has been visited by algorithm
  hasVisited(object) {
    return this.graphState.has(object) && this.graphState.get(object);
  }

  // run the next step of the algorithm
  step() {
    if (this.hasStarted && this.next.size === 0) {
      this.isComplete = true;
      return false;
    }

    let nextItem;

    if (this.hasStarted) {
      nextItem = this.next.dequeue();
    } else {
      this.hasStarted = true;
      nextItem = this.source;
    }

    if (nextItem instanceof Node) {
      this.visitNode(nextItem);
    } else if (nextItem instanceof Edge) {
      this.visitEdge(nextItem);
    } else {
      throw Error('Non-graph object in algorithm queue');
    }
    // represent the visual aspects of this step by creating a new step, adding a change for the current object, and completing the step
    this.stepBuilder.newStep(`Visiting ${nextItem.constructor.name} ${nextItem.id}`);
    this.stepBuilder.addChange(nextItem, {
      color: 'black'
    }, {
      color: 'red'
    }, {
      color: 'green'
    });
    this.stepBuilder.completeStep();

    return true;
  }

}

export { TraversalAlgorithm };
export default TraversalAlgorithm;

require('../scss/styles.scss');

import { Graph } from 'data/graph';
import * as Canvas from 'ui/canvas';
import * as UI from 'ui/ui';

let graph;

function init() {
  graph = new Graph();
  Canvas.init(graph);
  Canvas.resize();
  UI.init(graph);
  window.requestAnimationFrame(draw);
}

function draw() {
  Canvas.clear();
  graph.draw(Canvas.getContext());
  window.requestAnimationFrame(draw);
}

window.addEventListener('load', init, false);

window.addEventListener('resize', Canvas.resize, false);

import { Edge } from './edge';
import { drawArrows } from '../../util/curvedEdge';

export class DashedEdge extends Edge {

  draw(context) {
    context.strokeStyle = this.isSelected ? this.selectedColor : this.color;
    context.lineWidth = this.lineWidth;
    context.setLineDash([ 5, 5 ]);

    // Create a new path
    context.beginPath();

    // Start path at given point
    context.moveTo(this.startPoint.x, this.startPoint.y);

    // Draw line to given point
    context.quadraticCurveTo(this.bezierPoint.x, this.bezierPoint.y, this.destPoint.x, this.destPoint.y);

    // Draw to the canvas
    context.stroke();
    context.setLineDash([]);

    if (this.isDirected) {
      context.fillStyle = this.isSelected ? this.selectedColor : this.color;
      drawArrows(this, false, true);
    }

    if (this.edgeLabel !== '') {
      this.drawLabel(context);
    }
  }

}

import { calcBezierDistance, EDGE_DISTANCE_THRESHOLD } from '../../util/curvedEdge';
import { CircleNode } from '../node/circle-node';

/*
* Edge Class
*   Represents an edge in the graph, and is also responsible for supplying draw functionality
*
*   Constructor accepts at least two parameters, startNode and destNode are mandatory, all others have default values, bezierPoint just becomes line midpoint
*
*   For the sake of organization and simplicity of use, try to only use the following members and functions
*   Doing so will ensure that this class is used as intended, and also that our code is clean and organized
*
*   startNode
*   destNode
*   bezierPoint - returns a {x:,y:} object
*   isDirected
*   weight - get and set functions have been overloaded to hide confusing internals, can be assigned a number or a grammar string, access as a data member ie edge.weight = blah or blah = edge.weight
*   getCostType() - returns whether or not this edge has a "Grammar", "Distance", or "None" type of cost associated with it
*   getEndpoints(reference) - reference can be a node or a number(1,2), returns associated endpoint as {x:,y:}
*   setEndpoints(reference, endpoint) - reference same as getEndpoints, enpoints must be a {x:,y:}
*
*   TODO:  implement better directed functionality, move in curvedEdge functionality
*/
export class Edge {

  static numEdges = 0;
  id = Edge.numEdges++;

  // graph data
  startNode;
  destNode;
  isDirected;

  // status
  isSelected = false;

  // label
  xText;
  yText;
  edgeLabel = '';
  labelFont = '14px Arial';
  labelColor = 'black';
  showTextCtrl = false;
  cost;
  costType;

  // appearance
  color = 'black';
  selectedColor = 'red';
  lineWidth = 1;

  constructor(startNode, destNode, bezierPoint = null, cost = null, isDirected = false) {
    let methods = [
      'draw'
    ];

    for (let method of methods) {
      if (typeof this[method] === 'undefined' || typeof this[method] !== 'function') {
        throw TypeError('Must override method: ' + method);
      }
    }

    if (arguments.length < 2) {
      throw Error(`Edge constructor requires at least two arguments: startNode and destNode. Actually passed in ${arguments}`);
    }
    this.costType = null;
    this.startNode = startNode;
    this.destNode = destNode;
    this.bezierPoint = bezierPoint;
    this.cost = cost;
    this.isDirected = isDirected;
    this.isSelected = false;
    this.partners = null;
    for (let edge of this.startNode.edges) {
      if (edge.startNode.id === this.startNode.id && edge.destNode.id === this.destNode.id) {
        this.partners = edge.partners.slice(0);
        break;
      } else if (edge.destNode.id === this.startNode.id && edge.startNode.id === this.destNode.id) {
        this.partners = edge.partners.slice(0);
        break;
      }
    }
    if (this.partners === null) {
      this.partners = [ this ];
    } else {
      for (let i = 0; i < this.partners.length; i++) {
        this.partners[i].partners[this.partners[i].partners.length] = this;
      }
      this.partners[this.partners.length] = this;
    }

    startNode.edges.add(this);
    destNode.edges.add(this);


    if (this.startNode.id === this.destNode.id) {
      let r = CircleNode.radius;
      this.startPoint = this.startNode.getAnglePoint(240);
      this.destPoint = this.startNode.getAnglePoint(300);
      this.bezierPoint = {
        x: (this.startPoint.x + this.destPoint.x) / 2,
        y: this.startPoint.y - 2 * r
      };
      this.isDirected = true;
    } else {
      let incline = Math.asin((this.destNode.y - this.startNode.y) / Math.sqrt((this.destNode.x - this.startNode.x) * (this.destNode.x - this.startNode.x) + (this.destNode.y - this.startNode.y) * (this.destNode.y - this.startNode.y)));
      incline = incline * 180 / Math.PI;
      if (this.startNode.x >= this.destNode.x) {
        incline = 180 - incline;
      }
      incline = (incline + 360) % 360;
      let numPartners = this.partners.length + 1;
      for (let i = 0; i < this.partners.length; i++) {
        let startAngle = incline + 90 * (i + 1) / numPartners - 45;
        let destAngle = incline + 225 - 90 * (i + 1) / numPartners;
        try {
          this.partners[i].startPoint = this.startNode.getAnglePoint(startAngle);
          this.partners[i].destPoint = this.destNode.getAnglePoint(destAngle);
        } catch (e) {
          return;
        }
        let orient = (i + 1) / numPartners - 0.5;
        let diff = {
          x: this.partners[i].destPoint.x - this.partners[i].startPoint.x,
          y: this.partners[i].destPoint.y - this.partners[i].startPoint.y
        };
        this.partners[i].bezierPoint = {
          x: (this.partners[i].startPoint.x + this.partners[i].destPoint.x) / 2 - orient * diff.y,
          y: (this.partners[i].startPoint.y + this.partners[i].destPoint.y) / 2 + orient * diff.x
        };
      }
    }

    if (typeof cost === 'string' || cost instanceof String) {
      this.costType = false;
    } else if (typeof cost === 'number') {
      this.costType = true;
    }

    this.generateDefaultTextLocation();
    this.showTextCtrl = false;
  }

  detach() {
    this.startNode.edges.delete(this);
    this.destNode.edges.delete(this);
    this.startNode = null;
    this.destNode = null;
  }

  getCostType() {
    if (this.costType === false) {
      return 'Grammar';
    } else if (this.costType === true) {
      return 'Distance';
    }
    return 'None';
  }

  set weight(cost) {
    if (typeof cost === 'string' || cost instanceof String) {
      this.costType = false;
      this.cost = cost;
    } else if (typeof cost === 'number') {
      this.costType = true;
      this.cost = cost;
    } else if (cost === null) {
      this.costType = null;
      this.cost = null;
    } else {
      throw Error('cost must be a s(S)tring or number');
    }
  }

  get weight() {
    return this.cost;
  }

  getEndpoints(reference) {
    if (reference === this.startNode || reference === 1) {
      return this.startPoint;
    } else if (reference === this.endNode || reference === 2) {
      return this.destPoint;
    }
  }

  setEndpoints(reference, endpoint) {
    if (reference === this.startNode || reference === 1) {
      this.startPoint = endpoint;
    } else if (reference === this.endNode || reference === 2) {
      this.destPoint = endpoint;
    }
  }

  updateEndpoints() {
    if (this.startNode.id === this.destNode.id) {
      let r = CircleNode.radius;
      this.startPoint = this.startNode.getAnglePoint(240);
      this.destPoint = this.startNode.getAnglePoint(300);
      this.bezierPoint = {
        x: (this.startPoint.x + this.destPoint.x) / 2,
        y: this.startPoint.y - 2 * r
      };
      this.isDirected = true;
    } else {
      let incline = Math.asin((this.destNode.y - this.startNode.y) / Math.sqrt((this.destNode.x - this.startNode.x) * (this.destNode.x - this.startNode.x) + (this.destNode.y - this.startNode.y) * (this.destNode.y - this.startNode.y)));
      incline = incline * 180 / Math.PI;
      if (this.startNode.x >= this.destNode.x) {
        incline = 180 - incline;
      }
      incline = (incline + 360) % 360;
      let numPartners = this.partners.length + 1;
      let multiIndex = -1;
      for (let i = 0; i < this.partners.length; i++) {
        if (this.partners[i].id === this.id) {
          multiIndex = i + 1;
          break;
        }
      }
      let startAngle = incline + 90 * multiIndex / numPartners - 45;
      let destAngle = incline + 225 - 90 * multiIndex / numPartners;
      try {
        this.startPoint = this.startNode.getAnglePoint(startAngle);
        this.destPoint = this.destNode.getAnglePoint(destAngle);
      } catch (e) {
        return;
      }
      let orient = multiIndex / numPartners - 0.5;
      let diff = {
        x: this.destPoint.x - this.startPoint.x,
        y: this.destPoint.y - this.startPoint.y
      };
      this.bezierPoint = {
        x: (this.startPoint.x + this.destPoint.x) / 2 - orient * diff.y,
        y: (this.startPoint.y + this.destPoint.y) / 2 + orient * diff.x
      };
    }
  }

  containsPoint(x, y) {
    return EDGE_DISTANCE_THRESHOLD > calcBezierDistance(x, y, this.startPoint.x, this.startPoint.y, this.bezierPoint.x, this.bezierPoint.y, this.destPoint.x, this.destPoint.y);
  }

  draw(context) {
    throw Error('Can\'t call draw from abstract Edge class.');
  }

  drawLabel(context) {
    context.font = this.labelFont;
    context.fillStyle = this.labelColor;
    context.fillText(this.edgeLabel, this.xText, this.yText);
    if (this.showTextCtrl) {
      context.fillStyle = 'red';
      context.beginPath();
      context.arc(this.xText, this.yText, 3.0, 0, 1.5 * Math.PI);
      context.lineTo(this.xText, this.yText);
      context.fill();
    }
  }

  // find the starting point of our text box
  generateDefaultTextLocation() {
    // var xOffSet = context.measureText(this.edgeLabel)/2;
    // var yOffSet = 1; //assuming an edge is just 1 pixel

