import { Tool } from '../tool/tool';
import { Node } from '../data/node/node';
import TraversalAlgorithm from '../algorithm/traversal-algorithm';
import * as AlgorithmInterface from '../ui/algorithm';

export class AlgorithmTool extends Tool {

  name = 'Algorithm Tool';
  sidebarType = 'algorithm';

  currentMode = 'traversal';
  static modes = {
    traversal: TraversalAlgorithm
  };

  hasModes() {
    return true;
  }

  changeMode(mode) {
    this.currentMode = mode;
    AlgorithmInterface.setAlgorithm(AlgorithmTool.modes[mode]);
  }

  selectObject(event, graph, obj, x, y) {
    if (obj instanceof Node) {
      AlgorithmInterface.selectObject(obj);
    }
  }

}

import { Tool } from '../tool/tool';
import { Node } from '../data/node/node';
import { SolidEdge } from '../data/edge/solid-edge';
import { DashedEdge } from '../data/edge/dashed-edge';

export class EdgeTool extends Tool {

  name = 'Edge Tool';
  sidebarType = 'edge';

  currentMode = 'solid';
  static modes = {
    solid: SolidEdge,
    dashed: DashedEdge
  };

  hasModes() {
    return true;
  }

  start = null;
  dest = null;

  cancel() {
    this.deselect();
  }

  deselect() {
    if (this.start) {
      this.start.isSelected = false;
    }
    this.start = null;
    this.dest = null;
  }

  selectNode(graph, node) {
    let EdgeClass = EdgeTool.modes[this.currentMode];
    if (this.start === null) {
      this.start = node;
      this.start.isSelected = true;
    } else {
      this.dest = node;
      graph.addEdge(new EdgeClass(this.start, this.dest));
      this.start.isSelected = false;
      this.start = null;
      this.dest = null;
    }
  }

  selectObject(event, graph, obj, x, y) {
    if (obj instanceof Node) {
      this.selectNode(graph, obj);
    }
  }

  selectNone(event, graph, x, y) {
    this.deselect();
  }

}

import { Tool } from '../tool/tool';
import { Edge } from '../data/edge/edge';

export class EditEdgeTool extends Tool {

  name = 'Edit Edge Tool';

  currentMode = 'directed';
  static modes = {
    directed: 'isDirected'
  };

  hasModes() {
    return true;
  }

  selectObject(event, graph, obj, x, y) {
    if (obj instanceof Edge) {
      this.selectNone(event, graph, x, y);
      let edgeAttribute = EditEdgeTool.modes[this.currentMode];
      obj[edgeAttribute] = !obj[edgeAttribute];
    }
  }

  dragOverObject(event, graph, obj, x, y) {
    if (obj instanceof Edge) {
      this.selectNone(event, graph, x, y);
      let edgeAttribute = EditEdgeTool.modes[this.currentMode];
      obj[edgeAttribute] = !obj[edgeAttribute];
    }
  }

}

