// the following two functions adapted from: http:// stackoverflow.com / questions / 27176423 / function-to-solve-cubic-equation-analytically
// calculates cube root
// self-explanatory
function cuberoot(x) {
  let y = Math.pow(Math.abs(x), 1 / 3);
  return x < 0 ? -y : y;
}

function solveCubic(_a, _b, _c, _d) {
  let a = _a;
  let b = _b;
  let c = _c;
  let d = _d;

  if (Math.abs(a) < 1e-8) { // Quadratic case, ax^2 + bx + c=0
    a = b; b = c; c = d;
    if (Math.abs(a) < 1e-8) { // Linear case, ax + b=0
      a = b; b = c;
      if (Math.abs(a) < 1e-8) {// Degenerate case
        return [];
      }
      return [ -b / a ];
    }

    let D = b * b - 4 * a * c;
    if (Math.abs(D) < 1e-8) {
      return [ -b / (2 * a) ];
    } else if (D > 0) {
      return [ (-b + Math.sqrt(D)) / (2 * a), (-b - Math.sqrt(D)) / (2 * a) ];
    }
    return [];
  }

  // Convert to depressed cubic t^3 + pt + q = 0 (subst x = t - b / 3a)
  let p = (3 * a * c - b * b) / (3 * a * a);
  let q = (2 * b * b * b - 9 * a * b * c + 27 * a * a * d) / (27 * a * a * a);
  let roots;

  if (Math.abs(p) < 1e-8) { // p = 0 -> t^3 = -q -> t = -q^1 / 3
    roots = [ cuberoot(-q) ];
  } else if (Math.abs(q) < 1e-8) { // q = 0 -> t^3 + pt = 0 -> t(t^2 + p)=0
    roots = [ 0 ].concat(p < 0 ? [ Math.sqrt(-p), -Math.sqrt(-p) ] : []);
  } else {
    let D = q * q / 4 + p * p * p / 27;
    if (Math.abs(D) < 1e-8) {       // D = 0 -> two roots
      roots = [
        -1.5 * q / p,
        3 * q / p
      ];
    } else if (D > 0) {             // Only one real root
      let u = cuberoot(-q / 2 - Math.sqrt(D));
      roots = [ u - p / (3 * u) ];
    } else {                        // D < 0, three roots, but needs to use complex numbers / trigonometric solution
      let u = 2 * Math.sqrt(-p / 3);
      let t = Math.acos(3 * q / p / u) / 3;  // D < 0 implies p < 0 and acos argument in [-1..1]
      let k = 2 * Math.PI / 3;
      roots = [
        u * Math.cos(t),
        u * Math.cos(t - k),
        u * Math.cos(t - 2 * k)
      ];
    }
  }

  // Convert back from depressed cubic
  for (let i = 0; i < roots.length; i++) {
    roots[i] -= b / (3 * a);
  }

  return roots;
}


// helper function that calculates the distance between a specified point and a quadratic bezier
export function calcBezierDistance(pointX, pointY, startX, startY, controlX, controlY, endX, endY) {
  // preliminary, commonly used values
  let aX = controlX - startX;
  let aY = controlY - startY;
  let bX = endX - controlX - aX;
  let bY = endY - controlY - aY;
  let mX = startX - pointX;
  let mY = startY - pointY;

  // coefficients for the cubic to be solved
  let a = bX * bX + bY * bY;
  let b = 3 * (aX * bX + aY * bY);
  let c = 2 * (aX * aX + aY * aY) + mX * bX + mY * bY;
  let d = mX * aX + mY * aY;
  let ans = solveCubic(a, b, c, d);

  // reject any that violates x = [0, 1]
  for (let i = ans.length - 1; i >= 0; i--) {
    if (ans[i] > 1 || ans[i] < 0) {
      ans.splice(i, 1);
    }
  }
  ans[ans.length] = 0;
  ans[ans.length] = 1; // edge cases

  // minimize dist
  let smallestDist = cvs.width + cvs.height;

  // curves are parametrized as:  P(t) = (1-t)²P0 + 2t(1-t)P1 +t²P2.
  for (let j = 0; j < ans.length; j++) {
    let t = ans[j];
    let curvePointX = (1 - t) * startX * (1 - t) + 2 * t * (1 - t) * controlX + t * t * endX;
    let curvePointY = (1 - t) * startY * (1 - t) + 2 * t * (1 - t) * controlY + t * t * endY;
    let tempDist = Math.sqrt((curvePointX - pointX) * (curvePointX - pointX) + (curvePointY - pointY) * (curvePointY - pointY));
    if (smallestDist > tempDist) {
      smallestDist = tempDist;
      // smallestX = curvePointX;
      // smallestY = curvePointY;
    }
  }

  return smallestDist;
}

export function bezierDerivative(t, startX, startY, controlX, controlY, endX, endY) {
  return {
    x: (2 * t - 2) * startX + (2 - 4 * t) * controlX + 2 * t * endX,
    y: (2 * t - 2) * startY + (2 - 4 * t) * controlY + 2 * t * endY
  };
}

export function bezierPoint(t, startX, startY, controlX, controlY, endX, endY) {
  return {
    x: (1 - t) * (1 - t) * startX + 2 * (1 - t) * t * controlX + t * t * endX,
    y: (1 - t) * (1 - t) * startY + 2 * (1 - t) * t * controlY + t * t * endY
  };
}

export function drawArrows(edge, start, end) {
  let slope;
  let length;
  if (start) {
    slope = bezierDerivative(0, edge.startPoint.x, edge.startPoint.y, edge.bezierPoint.x,
                  edge.bezierPoint.y, edge.destPoint.x, edge.destPoint.y);
    length = Math.sqrt(slope.x * slope.x + slope.y * slope.y);
    // normalize slope
    slope = { x: slope.x / length, y: slope.y / length };
    // perpendicular:
    ctx.beginPath();
    ctx.moveTo(edge.startPoint.x, edge.startPoint.y);
    ctx.lineTo(edge.startPoint.x + 15 * slope.x - 5 * slope.y,
             edge.startPoint.y + 15 * slope.y + 5 * slope.x);
    ctx.lineTo(edge.startPoint.x + 9 * slope.x, edge.startPoint.y + 9 * slope.y);
    ctx.lineTo(edge.startPoint.x + 15 * slope.x + 5 * slope.y,
             edge.startPoint.y + 15 * slope.y - 5 * slope.x);
    ctx.closePath();
    ctx.fill();
  }
  if (end) {
    slope = bezierDerivative(1, edge.startPoint.x, edge.startPoint.y, edge.bezierPoint.x,
                  edge.bezierPoint.y, edge.destPoint.x, edge.destPoint.y);
    length = Math.sqrt(slope.x * slope.x + slope.y * slope.y);
    // normalize slope
    slope = { x: slope.x / length, y: slope.y / length };
    // perpendicular:
    ctx.beginPath();
    ctx.moveTo(edge.destPoint.x, edge.destPoint.y);
    ctx.lineTo(edge.destPoint.x - 15 * slope.x - 5 * slope.y,
             edge.destPoint.y - 15 * slope.y + 5 * slope.x);
    ctx.lineTo(edge.destPoint.x - 9 * slope.x, edge.destPoint.y - 9 * slope.y);
    ctx.lineTo(edge.destPoint.x - 15 * slope.x + 5 * slope.y,
             edge.destPoint.y - 15 * slope.y - 5 * slope.x);
    ctx.fill();
  }
}

export function drawLabel(edge, label) {
  let slope = bezierDerivative(0.5, edge.startPoint.x, edge.startPoint.y, edge.bezierPoint.x,
                  edge.bezierPoint.y, edge.destPoint.x, edge.destPoint.y);
  let length = Math.sqrt(slope.x * slope.x + slope.y * slope.y);
  slope = { x: slope.x / length, y: slope.y / length };
  let point = bezierPoint(0.5, edge.startPoint.x, edge.startPoint.y, edge.bezierPoint.x,
                 edge.bezierPoint.y, edge.destPoint.x, edge.destPoint.y);
  ctx.font = '12px Arial';
  ctx.fillText(label, point.x + 12 * slope.y, point.y - 12 * slope.x);
}

export function calculateLoop(node, angle) {
  // let edge = [];
  let theta = Math.PI * angle / 180;
  let r = DEFAULT_RADIUS;
  return new Edge(node, node,
       r * Math.cos(theta) + node.x, r * Math.sin(theta) + node.y,
       4 * r * Math.cos(theta + Math.PI / 8) + node.x, 4 * r * Math.sin(theta + Math.PI / 8) + node.y,
       r * Math.cos(theta + Math.PI / 4) + node.x, r * Math.sin(theta + Math.PI / 4) + node.y);
}

export function drawCurvedEdge(startx, starty, destx, desty, numEdge, rad) {
  let canvas = document.getElementById('canvas');
  let context = canvas.getContext('2d');
  let c1x = startx;
  let c1y = starty;
  let c2x = destx;
  let c2y = destx;
  let edgeNum = numEdge;
  let r = rad;

  context.beginPath();
  context.arc(c1x, c1y, r, 0, 2 * Math.PI);
  context.stroke();
  context.beginPath();
  context.arc(c2x, c2y, r, 0, 2 * Math.PI);
  context.stroke();
  let c2xtrans = c2x - c1x;
  let c2ytrans = c2y - c1y;
  // let c1xtrans = 0;
  // let c1ytrans = 0;
  let theta = Math.asin(c2ytrans / Math.sqrt(c2xtrans * c2xtrans + c2ytrans * c2ytrans));
  // let c1xrot = 0;
  // let c1yrot = 0;
  let c2xrot = c2xtrans * Math.cos(-theta) - c2ytrans * Math.sin(-theta);
  // let c2yrot = 0;

  let edgeData = [];
  for (let i = 0; i < edgeNum; i++) {
    let currTheta = Math.PI * (i + 1) / (edgeNum + 1) - Math.PI / 2;
    edgeData[i] = [
      r * Math.cos(currTheta),
      r * Math.sin(currTheta),
      c2xrot / 2,
      c2xrot * (2 * currTheta / Math.PI),
      c2xrot - r * Math.cos(currTheta),
      r * Math.sin(currTheta)
    ];
    // rotate and translate back
    for (let j = 0; j < 3; j++) {
      let tempX = edgeData[i][2 * j] * Math.cos(theta) - edgeData[i][2 * j + 1] * Math.sin(theta) + c1x;
      let tempY = edgeData[i][2 * j] * Math.sin(theta) + edgeData[i][2 * j + 1] * Math.cos(theta) + c1y;
      edgeData[i][2 * j] = tempX;
      edgeData[i][2 * j + 1] = tempY;
    }
    context.beginPath();
    context.moveTo(edgeData[i][0], edgeData[i][1]);
    context.quadraticCurveTo(edgeData[i][2], edgeData[i][3], edgeData[i][4], edgeData[i][5]);
    context.stroke();
  }
}

import * as UI from '../ui/ui';
import * as Sidebar from '../ui/sidebar';
import { CircleNode } from '../data/node/circle-node';
import { SquareNode } from '../data/node/square-node';

export class MouseHandler {

  // distance the mouse needs to move to start a drag
  // this prevents accidentally dragging a few pixels when actually trying to click
  DRAG_THRESHOLD = 7;

  selectedObject = null;
  draggedObject = null;

  clickStartX = null;
  clickStartY = null;
  mousePressed = false;
  isDragging = false;

  constructor(graph) {
    this.graph = graph;
  }

  downListener(event, currentTool, x, y) {
    this.mousePressed = true;
    this.clickStartX = x;
    this.clickStartY = y;

    if (this.graph.hasComponent(x, y)) {
      let component = this.graph.getComponent(x, y);
      if (currentTool.preSelectObject(event, this.graph, component, x, y)) {
        this.selectedObject = component;
      } else {
        this.selectedObject = null;
      }
    } else {
      currentTool.preSelectNone(this.graph, x, y);
      this.selectedObject = null;
    }
  }

  upListener(event, currentTool, x, y) {
    // check if dragging
    if (this.isDragging) {
      this.isDragging = false;

      // drop object
      if (this.graph.hasComponent(x, y, this.draggedObject)) {
        currentTool.dropOnObject(event, this.graph, this.draggedObject, this.graph.getComponent(x, y), this.clickStartX, this.clickStartY, x, y);
      } else {
        currentTool.dropOnNone(event, this.graph, this.draggedObject, this.clickStartX, this.clickStartY, x, y);
      }
      this.draggedObject = null;
    } else {
      // click
      let component = null;
      if (this.graph.hasComponent(x, y)) {
        component = this.graph.getComponent(x, y);
      }
      if (component === this.selectedObject) {
        if (component) {
          currentTool.selectObject(event, this.graph, component, x, y);
          Sidebar.updateSidebar(component);
        } else {
          currentTool.selectNone(event, this.graph, x, y);
          Sidebar.updateSidebar();
        }
      } else {
        currentTool.abortSelect(this.graph, x, y);
      }
    }
    this.mousePressed = false;
    this.selectedObject = null;
  }

  moveListener(event, currentTool, x, y) {
    UI.updateMouse(x, y);

    if (!this.isDragging) {
      // check for dragging
      if (this.mousePressed) {
        // check if mouse movement passes threshold
        let dx = x - this.clickStartX;
        let dy = y - this.clickStartY;

        if (Math.sqrt(dx * dx + dy * dy) >= this.DRAG_THRESHOLD) {
          this.isDragging = true;
          if (this.selectedObject !== null) {
            if (currentTool.preDragObject(event, this.graph, this.selectedObject, x, y)) {
              this.draggedObject = this.selectedObject;
            } else {
              this.selectedObject = null;
              this.draggedObject = null;
            }
          } else {
            currentTool.preDragNone(this.graph, x, y);
            this.draggedObject = this.selectedObject;
          }
        }
      } else {
        // regular move
        // hover effect?
      }
    } else if (this.draggedObject) {
      // handle dragging object
      currentTool.dragObject(event, this.graph, this.draggedObject, this.clickStartX, this.clickStartY, x, y);
      Sidebar.updateSidebar(this.draggedObject);
    } else if (this.graph.hasComponent(x, y)) {
      // handle dragging over object
      currentTool.dragOverObject(event, this.graph, this.graph.getComponent(x, y), this.clickStartX, this.clickStartY, x, y);
      Sidebar.updateSidebar();
    } else {
      // handle dragging empty space
      currentTool.dragNone(event, this.graph, this.clickStartX, this.clickStartY, x, y);
    }
  }

  contextComponent(event, x, y) {
    let component = null;
    if (this.graph.hasComponent(x, y)) {
      component = this.graph.getComponent(x, y);
    }

    return component;
  }

  contextAdd(arg, x, y) {
    let modes = {
      circle: CircleNode,
      square: SquareNode
    };

    let NodeClass = modes[arg];
    let node = new NodeClass(x, y);

    if (!this.graph.isNodeCollision(node, x, y)) {
      this.graph.addNode(node);
    }
  }

  contextToggle(arg, component) {
    component[arg] = !component[arg];
  }

  contextDelete(arg, component) {
    if (arg === 'node') {
      this.graph.removeNode(component);
    } else if (arg === 'edge') {
      this.graph.removeEdge(component);
    }
  }

  contextSelect(event, currentTool, component, x, y) {
    currentTool.selectObject(event, this.graph, component, x, y);
  }
}

/*
 * Based on Queue.js by Stephen Morley - http://code.stephenmorley.org/
 */

/* Creates a new queue. A queue is a first-in-first-out (FIFO) data structure -
 * items are added to the end of the queue and removed from the front.
 */
export default class Queue {

  constructor() {
    // initialise the queue and offset
    this.queue = [];
    this.offset = 0;
  }

  // Getter for size, allows access as a property)
  get size() {
    return this.queue.length - this.offset;
  }

  // Clear the queue
  clear() {
    this.queue = [];
    this.offset = 0;
  }

  /* Enqueues the specified item. The parameter is:
   *
   * item - the item to enqueue
   */
  enqueue(item) {
    if (typeof item === 'undefined' || item === null) {
      throw Error('Item must be defined and non-null');
    }
    this.queue.push(item);
  }

  /* Dequeues an item and returns it. If the queue is empty, the value
   * 'null' is returned.
   */
  dequeue() {
    // if the queue is empty, return immediately
    if (this.queue.length === 0) {
      return null;
    }

    // store the item at the front of the queue
    let item = this.queue[this.offset];

    // increment the offset and remove the free space if necessary
    if (++this.offset * 2 >= this.queue.length) {
      this.queue = this.queue.slice(this.offset);
      this.offset = 0;
    }

    // return the dequeued item
    return item;
  }

  /* Returns the item at the front of the queue (without dequeuing it). If the
   * queue is empty then null is returned.
   */
  peek() {
    return this.queue.length > 0 ? this.queue[this.offset] : null;
  }

  has(item) {
    if (typeof item === 'undefined' || item === null) {
      throw Error('Item must be defined and non-null');
    }
    for (let i = this.offset; i < this.queue.length; i++) {
      if (this.queue[i] === item) {
        return true;
      }
    }
    return false;
  }

  forEach(callback) {
    for (let i = this.offset; i < this.queue.length; i++) {
      if (callback(this.queue[i]) === false) {
        break;
      }
    }
  }

}

export default class Stack {

  constructor() {
    this.stack = [];
  }

  // Getter for size, allows access as a property
  get size() {
    return this.stack.length;
  }

  clear() {
    this.stack = [];
  }

  push(item) {
    if (typeof item === 'undefined' || item === null) {
      throw Error('Item must be defined and non-null');
    }
    this.stack.push(item);
  }

  pop() {
    if (this.stack.length === 0) {
      return null;
    }

    return this.stack.pop();
  }

  peek() {
    return this.stack.length > 0 ? this.stack[this.stack.length - 1] : null;
  }

  has(item) {
    if (typeof item === 'undefined' || item === null) {
      throw Error('Item must be defined and non-null');
    }
    for (let i = 0; i < this.stack.length; i++) {
      if (this.stack[i] === item) {
        return true;
      }
    }
    return false;
  }

  forEach(callback) {
    for (let i = this.stack.length - 1; i >= 0; i--) {
    // for (let i = 0; i < this.stack.length; i++) {
      if (callback(this.stack[i]) === false) {
        break;
      }
    }
  }

}

